const renderErrors = (errors: any, parentKey = ""): JSX.Element[] => {
  return Object.entries(errors).flatMap(([key, error]: any) => {
    const fieldPath = parentKey ? `${parentKey}.${key}` : key;

    if (error?.message) {
      return (
        <li key={fieldPath}>
