                          <div>
                            <Label>Position</Label>
                            <Input
                              {...form.register(`employmentHistory.${index}.position`)} placeholder="e.g., Director ICT Officer"/>
                          </div>

                          <div>
                            <Label>Start Date</Label>
                            <Input
                              type="date"
                              {...form.register(`employmentHistory.${index}.startDate`)}
                            />
                          </div>

                          <div>
                            <Label>End Date</Label>
