      icon: Users, 
      label: 'Applications', 
      description: 'Review applications' 
    },
    { 
      href: '/admin/reports', 
      icon: BarChart3, 
      label: 'Reports', 
      description: 'Generate reports' 
    },
    { 
      href: '/admin/notifications', 
      icon: Bell, 
      label: 'Notifications', 
      description: 'Send notifications' 
    },
    { 
      href: '/admin/settings', 
      icon: Settings, 
      label: 'System Config', 
      description: 'System settings' 
    },
  ];

  const boardNavItems = [
    { 
      href: '/board', 
      icon: LayoutDashboard, 
      label: 'Dashboard', 
