        return applicantNavItems;
      case 'admin':
        return adminNavItems;
      case 'board':
        return boardNavItems;
      default:
        return [];
    }
  };

  const getRoleColor = () => {
    switch (userRole) {
      case 'applicant':
        return 'bg-blue-100 text-blue-800';
      case 'admin':
        return 'bg-red-100 text-red-800';
      case 'board':
        return 'bg-green-100 text-green-800';
