import { db } from "./db";
import { eq, inArray, Table, Column } from "drizzle-orm";
import { normalizeDates, autoDateFields } from "../client/src/lib/sanitizeDates";

interface UpsertOptions<T> {
  table: Table;
  primaryKey: Column<any>;
  foreignKey: Column<any>;
  foreignKeyValue: number | string;
  records: T[];
  label?: string;
  diagnostics?: boolean;
}

export async function upsertByPKAndFK<T extends Record<string, any>>({
  table,
  primaryKey,
  foreignKey,
  foreignKeyValue,
  records,
  label,
  diagnostics = false,
}: UpsertOptions<T>): Promise<{
  success: boolean;
  inserted: number;
  updated: number;
  deleted: number;
}> {
  const tableLabel = label ?? (table as any)._?.name ?? "unknown";
  const dateFields = autoDateFields[tableLabel] || [];

  if (diagnostics) {
    console.log(`ðŸ”„ Upserting ${records.length} records into ${tableLabel}`);
  }

  return await db.transaction(async (trx) => {
    // 1ï¸âƒ£ Fetch existing records for this applicant
    const existing = await trx
      .select({ id: primaryKey })
      .from(table)
      .where(eq(foreignKey, foreignKeyValue));

    const existingIds = new Set(existing.map((r) => r.id));
    const incomingIds = new Set(records.map((r) => r.id).filter(Boolean));

    // 2ï¸âƒ£ Delete only rows that are no longer in incoming payload
    const idsToDelete = Array.from(existingIds).filter(
      (id) => !incomingIds.has(id)
    );
      if (idsToDelete.length > 0) {
        console.log(`ðŸ—‘ï¸ Deleting ${idsToDelete.length} IDs from ${tableLabel}`);
      //   await trx.delete(table).where(inArray(primaryKey, idsToDelete));
      if (diagnostics) console.log("ðŸ—‘ï¸ Deleted IDs:", idsToDelete);
    }

    // 3ï¸âƒ£ Insert or Update
    let inserted = 0;
    let updated = 0;

    for (const r of records) {
      const payload = normalizeDates(
        { ...r, [foreignKey.name]: foreignKeyValue },
        { dateFields, setUpdatedAt: true }
      );

      if (r.id && existingIds.has(r.id)) {
        await trx.update(table).set(payload).where(eq(primaryKey, r.id));
        updated++;
        if (diagnostics) console.log(`âœï¸ Updated row id=${r.id}`, payload);
      } else {
        await trx.insert(table).values(payload);
        inserted++;
        if (diagnostics) console.log(`âž• Inserted new row ${table}`, payload);
      }
    }

    return { success: true, inserted, updated, deleted: idsToDelete.length };
  });
}

server/upsertRecord.ts.content:
import { db } from "./db";
import { eq, inArray, Table, Column } from "drizzle-orm";
import { normalizeDates, autoDateFields } from "../client/src/lib/sanitizeDates";

// ðŸ”¹ Option A: Table WITH FK
interface UpsertWithFK<T> {
  table: Table;
  records: T[];
  primaryKeyColumn: Column<any>;        // PK required
  foreignKeyColumn: Column<any>;        // FK required
  foreignKeyValue: number | string;     // e.g. applicantId
  diagnostics?: boolean;
  label?: string;
}

// ðŸ”¹ Option B: Table WITHOUT FK
interface UpsertWithoutFK<T> {
  table: Table;
  records: T[];
  primaryKeyColumn: Column<any>;        // PK required
  diagnostics?: boolean;
  label?: string;
}

// ðŸ”¹ Union type
type UpsertOptions<T> = UpsertWithFK<T> | UpsertWithoutFK<T>;

/**
 * Generic upsert helper
 * - Deletes rows that are missing in incoming payload
 * - Updates rows that have an `id`
 * - Inserts rows that donâ€™t have an `id`
 */
export async function upsertRecordsByPK<T extends Record<string, any>>(
  options: UpsertOptions<T>
): Promise<{ success: boolean; inserted: number; updated: number; deleted: number }> {
  const { table, records, primaryKeyColumn, diagnostics = false, label } = options;

  const hasFK = "foreignKeyColumn" in options;
  const foreignKeyColumn = hasFK ? options.foreignKeyColumn : undefined;
  const foreignKeyValue = hasFK ? options.foreignKeyValue : undefined;

  const tableLabel = label ?? (table as any)._?.name ?? "unknown";
  const dateFields = autoDateFields[tableLabel] || [];

  if (diagnostics) {
    console.log(`ðŸ”„ Upserting ${records.length} records into ${tableLabel}`);
  }

  return await db.transaction(async (trx) => {
    // 1ï¸âƒ£ Fetch existing IDs
    let existing: { id: any }[] = [];
    if (hasFK && foreignKeyColumn && foreignKeyValue !== undefined) {
      existing = await trx
        .select({ id: primaryKeyColumn })
        .from(table)
        .where(eq(foreignKeyColumn, foreignKeyValue));
    } else {
      existing = await trx.select({ id: primaryKeyColumn }).from(table);
    }

    const existingIds = new Set(existing.map((r) => r.id));
    const incomingIds = new Set(records.map((r) => r.id).filter(Boolean));

    // 2ï¸âƒ£ Delete rows not in incoming payload
    const idsToDelete = [...existingIds].filter((id) => !incomingIds.has(id));
    if (idsToDelete.length > 0) {
      await trx.delete(table).where(inArray(primaryKeyColumn, idsToDelete));
      if (diagnostics) console.log("ðŸ—‘ï¸ Deleted IDs:", idsToDelete);
    }

    // 3ï¸âƒ£ Insert/Update
    let insertedCount = 0;
    let updatedCount = 0;

    for (const r of records) {
      const payload = normalizeDates(
        hasFK && foreignKeyColumn && foreignKeyValue !== undefined
          ? { ...r, [foreignKeyColumn.name]: foreignKeyValue }
          : { ...r },
        { dateFields, setUpdatedAt: true }
      );

      if (r.id && existingIds.has(r.id)) {
        await trx.update(table).set(payload).where(eq(primaryKeyColumn, r.id));
        updatedCount++;
        if (diagnostics) console.log(`âœï¸ Updated row id=${r.id}`);
      } else {
        await trx.insert(table).values(payload);
        insertedCount++;
        if (diagnostics) console.log("âž• Inserted new row:", payload);
      }
    }

    return {
      success: true,
      inserted: insertedCount,
      updated: updatedCount,
      deleted: idsToDelete.length,
    };
  });
}

