                                </SelectContent>
                              </Select>
                            </div>
                            <div>
                              <Label htmlFor="ward-name">Ward Name</Label>
                              <Input 
                                id="ward-name" 
                                {...wardForm.register('name')} 
                                placeholder="Enter ward name"
                              />
                              {wardForm.formState.errors.name && (
