import type { Express } from "express";
import { createServer, type Server } from "http";
import express from "express";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { z } from "zod";
import multer from "multer";
import path from "path";
import bcrypt from "bcrypt";
import { insertJobSchema, insertApplicationSchema, insertNoticeSchema, users } from "@shared/schema";
import passport from "passport";
import { sendOtpHandler, verifyOtpHandler } from "../client/src/lib/africastalking-sms";

// OTP utility functions
function generateOtp(): string {
  return Math.floor(100000 + Math.random() * 900000).toString();
}

// Simulate SMS sending (in production, integrate with SMS provider like Twilio)
function sendSms(phoneNumber: string, message: string): Promise<boolean> {
  console.log(`SMS to ${phoneNumber}: ${message}`);
  // In development, just log the OTP
  return Promise.resolve(true);
}

// File upload configuration
const upload = multer({
  dest: 'uploads/',
  limits: { fileSize: 10 * 1024 * 1024 }, // 10MB limit
  fileFilter: (req, file, cb) => {
    const allowedTypes = ['application/pdf', 'image/jpeg', 'image/jpg', 'image/png', 'application/msword', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'];
    if (allowedTypes.includes(file.mimetype)) {
      cb(null, true);
    } else {
      cb(new Error('Invalid file type'));
    }
  }
});
// Profile photo upload config with extensions preserved
const profilePhotoStorage = multer.diskStorage({
  destination: "uploads/profile-photos/",
  filename: (req, file, cb) => {
    const ext = path.extname(file.originalname); // .jpg / .png
    const uniqueName = `${Date.now()}-${Math.round(Math.random() * 1e9)}${ext}`;
    cb(null, uniqueName);
  },
});
// Profile photo upload configuration  
const profilePhotoUpload = multer({
  storage: profilePhotoStorage,
  limits: { fileSize: 5 * 1024 * 1024 }, // 5MB limit for profile photos
  fileFilter: (req, file, cb) => {
    const allowedTypes = ['image/jpeg', 'image/jpg', 'image/png'];
    if (allowedTypes.includes(file.mimetype)) {
      cb(null, true);
    } else {
      cb(new Error('Profile photo must be JPEG or PNG format'));
    }
  }
});

export async function registerRoutes(app: Express): Promise<Server> {
  await setupAuth(app);
  app.use('/uploads', express.static('uploads'));

  // Profile photo upload endpoint
  app.post('/api/upload/profile-photo', profilePhotoUpload.single('profilePhoto'), async (req, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ message: 'No file uploaded' });
      }

      // Basic validation
      const file = req.file;
      const allowedTypes = ['image/jpeg', 'image/jpg', 'image/png'];
      if (!allowedTypes.includes(file.mimetype)) {
        return res.status(400).json({ message: 'Invalid file type. Only JPEG and PNG are allowed.' });
      }

      // Generate file URL
      const fileUrl = `/uploads/profile-photos/${file.filename}`;

      res.json({ 
        message: 'Photo uploaded successfully',
        url: fileUrl,
        filename: file.filename
      });
    } catch (error) {
      console.error('Error uploading profile photo:', error);
      res.status(500).json({ message: 'Failed to upload photo' });
    }
  });

  // --- Signup ---
  app.post("/api/auth/signup", profilePhotoUpload.single("profilePhoto"), async (req, res) => {
    try {
      const { email, password, lastName, phoneNumber,idPassportNumber } = req.body;      
      const profilePhoto = req.file ? req.file.filename : "default.jpg";
      const fileUrl = `/uploads/profile-photos/${profilePhoto}`
      const isValidEmail = await storage.verifyEmail(email);
    if (isValidEmail) {
      return res.status(401).json({ message: `Your email ${email} already registered` });
    }
    if (!password) {
      return res.status(400).json({ message: "Password is required" });
    }
      const hashedPassword = await bcrypt.hash(password, 10);

      const newUser = await storage.upsertUser({
        ...req.body,
        password: hashedPassword,
        passwordHash: hashedPassword,
        lastName: lastName,
        phoneNumber,
        profileImageUrl: fileUrl,
        idPassportNumber
      });

      // Immediately log the user in
      req.login(newUser, (err) => {
        if (err) {
          console.error("Login after signup failed:", err);
          return res.status(500).json({ message: "Failed to login after signup" });
        }
        res.status(201).json({ user: newUser });
      });
    } catch (err: any) {
      console.error("Signup error:", err);
      res.status(500).json({ message: err.message || "Signup failed" });
    }
  });

  // --- Login ---
  app.post(
    "/api/auth/login",
    passport.authenticate("local"),
    (req, res) => {
      res.json({ user: req.user });
    }
  );

  // --- Logout ---
  app.post("/api/auth/logout", (req, res) => {
    req.logout(() => {
      res.json({ message: "Logged out" });
    });
  });
  app.get("/api/auth/me", (req, res) => {
  if (!req.user) {
    return res.status(401).json({ message: "Not authenticated" });
  }

  // Decide redirectUrl based on role or other logic
  // --- Get current session user ---
    let redirectUrl = "/"; // default

switch (req.user?.role) {
  case "admin":
    redirectUrl = "/admin";
    break;
  case "applicant":
    redirectUrl = "/dashboard";
    break;
  default:
    redirectUrl = "/";
    break;
}

  res.json({ user: req.user, redirectUrl });
});
// Employee verification routes
  app.post('/api/employee/verify', async (req, res) => {
    try {
      const { personalNumber, idNumber } = req.body;

      if (!personalNumber || !idNumber) {
        return res.status(400).json({ message: 'Personal number and ID number are required' });
      }

      // Check if employee exists with matching personal number and ID
      const employee = await storage.verifyEmployee(personalNumber, idNumber);

      if (!employee) {
        return res.status(404).json({ message: 'Employee not found or ID number does not match' });
      }

      res.json({ 
        message: 'Employee verified successfully',
        employee: {
          personalNumber: employee.personalNumber,
          designation: employee.designation,
        }
      });
    } catch (error) {
      console.error('Error verifying employee:', error);
      res.status(500).json({ message: 'Failed to verify employee' });
    }
  });

  app.post('/api/employee/details', isAuthenticated, async (req:any, res) => {
    try {
      const userId = req.user.id;
      const employeeData = req.body;

      // Get applicant profile first
      const applicant = await storage.getApplicant(userId);
      if (!applicant) {
        return res.status(404).json({ message: 'Applicant profile not found' });
      }

      // Create or update employee record
      const employee = await storage.upsertEmployeeDetails(applicant.id, employeeData);

      res.json({ 
        message: 'Employee details saved successfully',
        employee 
      });
    } catch (error) {
      console.error('Error saving employee details:', error);
      res.status(500).json({ message: 'Failed to save employee details' });
    }
  });
  // OTP routes

app.post("/api/auth/send-otp", sendOtpHandler);
app.post("/api/auth/verify-otp", verifyOtpHandler);

  app.post('/api/auth/send-otp', async (req, res) => {
    try {
      const { phoneNumber } = req.body;

      if (!phoneNumber) {
        return res.status(400).json({ message: 'Phone number is required' });
      }

      // Generate OTP
      const otp = generateOtp();

      // Store OTP in database
      await storage.createOtp(phoneNumber, otp);

      // Send SMS (in production, use real SMS service)
      const message = `Your TNCPSB verification code is: ${otp}. Valid for 5 minutes.`;
      await sendSms(phoneNumber, message);

      res.json({ message: 'OTP sent successfully' });
    } catch (error) {
      console.error('Error sending OTP:', error);
      res.status(500).json({ message: 'Failed to send OTP' });
    }
  });

  app.post('/api/auth/verify-otp', async (req, res) => {
    try {
      const { phoneNumber, otp } = req.body;

      if (!phoneNumber || !otp) {
        return res.status(400).json({ message: 'Phone number and OTP are required' });
      }

      const isValid = await storage.verifyOtp(phoneNumber, otp);

      if (isValid) {
        res.json({ message: 'OTP verified successfully', verified: true });
      } else {
        res.status(400).json({ message: 'Invalid or expired OTP', verified: false });
      }
    } catch (error) {
      console.error('Error verifying OTP:', error);
      res.status(500).json({ message: 'Failed to verify OTP' });
    }
  });

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      // Get applicant profile if user is an applicant
      let applicantProfile = null;
      if (user.role === 'applicant') {
        applicantProfile = await storage.getApplicant(userId);
      }

      // Determine redirect URL based on role and profile completion
      let redirectUrl = '/';
      if (user.role === 'applicant') {
        if (!applicantProfile) {
          redirectUrl = '/profile?step=1&reason=complete_profile';
        } else if ((applicantProfile.profileCompletionPercentage || 0) < 100) {
          redirectUrl = '/profile?step=2&reason=incomplete_profile';
        } else {
          redirectUrl = '/dashboard';
        }
      } else if (user.role === 'admin') {
        redirectUrl = '/admin';
      } else if (user.role === 'board') {
        redirectUrl = '/board';
      }

      res.json({ ...user, applicantProfile, redirectUrl });
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Public routes (no authentication required)

  // Get all counties for location dropdowns
  app.get('/api/public/counties', async (req, res) => {
    try {
      const counties = await storage.getCounties();
      res.json(counties);
    } catch (error) {
      console.error('Error fetching counties:', error);
      res.status(500).json({ message: 'Failed to fetch counties' });
    }
  });

  // Get constituencies by county
  app.get('/api/public/constituencies/:countyId', async (req, res) => {
    try {
      const countyId = parseInt(req.params.countyId);
      const constituencies = await storage.getConstituenciesByCounty(countyId);
      res.json(constituencies);
    } catch (error) {
      console.error('Error fetching constituencies:', error);
      res.status(500).json({ message: 'Failed to fetch constituencies' });
    }
  });

  // Get wards by constituency
  app.get('/api/public/wards/:constituencyId', async (req, res) => {
    try {
      const constituencyId = parseInt(req.params.constituencyId);
      const wards = await storage.getWardsByConstituency(constituencyId);
      res.json(wards);
    } catch (error) {
      console.error('Error fetching wards:', error);
      res.status(500).json({ message: 'Failed to fetch wards' });
    }
  });

  // Get published notices
  app.get('/api/public/notices', async (req, res) => {
    try {
      const notices = await storage.getNotices(true);
      res.json(notices);
    } catch (error) {
      console.error('Error fetching notices:', error);
      res.status(500).json({ message: 'Failed to fetch notices' });
    }
  });

  // Get active jobs
  app.get('/api/public/jobs', async (req, res) => {
    try {
      const departmentId = req.query.departmentId ? parseInt(req.query.departmentId as string) : undefined;
      const jobs = await storage.getJobs({ isActive: true, departmentId });
      res.json(jobs);
    } catch (error) {
      console.error('Error fetching jobs:', error);
      res.status(500).json({ message: 'Failed to fetch jobs' });
    }
  });

  // Get system configuration data
  app.get('/api/public/config', async (req, res) => {
    try {
      const [departments, designations, awards, courses] = await Promise.all([
        storage.getDepartments(),
        storage.getDesignations(),
        storage.getAwards(),
        storage.getCoursesOffered()
      ]);

      res.json({
        departments,
        designations,
        awards,
        courses
      });
    } catch (error) {
      console.error('Error fetching config:', error);
      res.status(500).json({ message: 'Failed to fetch configuration' });
    }
  });
  // Protected applicant routes  
  // Create applicant profile
  app.post('/api/applicant/profile', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const user = await storage.getUser(userId);

      if (!user || user.role !== 'applicant') {
        return res.status(403).json({ message: 'Access denied' });
      }

      // Check if profile already exists
      const existingProfile = await storage.getApplicant(userId);
      if (existingProfile) {
        return res.status(400).json({ message: 'Profile already exists' });
      }

      const profileData = {
        ...req.body,
        userId,
      };

      const profile = await storage.createApplicant(profileData);
      res.json(profile);
    } catch (error) {
      console.error('Error creating profile:', error);
      res.status(500).json({ message: 'Failed to create profile' });
    }
  });

  // Update applicant profile
  app.put('/api/applicant/profile', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const user = await storage.getUser(userId);

      if (!user || user.role !== 'applicant') {
        return res.status(403).json({ message: 'Access denied' });
      }
      const profile = await storage.getApplicant(userId);
      if (!profile) {
        return res.status(404).json({ message: 'Profile not found' });
      }

      const updatedProfile = await storage.updateApplicant(profile.id, req.body);
      res.json(updatedProfile);
    } catch (error) {
      console.error('Error updating profile:', error);
      res.status(500).json({ message: 'Failed to update profile' });
    }
  });

  // Mark phone as verified after OTP verification
  app.post('/api/applicant/verify-phone', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const user = await storage.getUser(userId);

      if (!user || user.role !== 'applicant') {
        return res.status(403).json({ message: 'Access denied' });
      }

      const profile = await storage.getApplicant(userId);
      if (!profile) {
        return res.status(404).json({ message: 'Profile not found' });
      }

      const { phoneNumber } = req.body;

      // Update phone verification status
      const updatedProfile = await storage.updateApplicant(profile.id, {
        phoneVerified: true,
        phoneVerifiedAt: new Date(),
        phoneNumber,
      });

      res.json(updatedProfile);
    } catch (error) {
      console.error('Error verifying phone:', error);
      res.status(500).json({ message: 'Failed to verify phone' });
    }
  });

  // Get applicant's applications
  app.get('/api/applicant/applications', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const user = await storage.getUser(userId);

      if (!user || user.role !== 'applicant') {
        return res.status(403).json({ message: 'Access denied' });
      }

      const profile = await storage.getApplicant(userId);
      if (!profile) {
        return res.status(404).json({ message: 'Profile not found' });
      }

      const applications = await storage.getApplications({ applicantId: profile.id });
      res.json(applications);
    } catch (error) {
      console.error('Error fetching applications:', error);
      res.status(500).json({ message: 'Failed to fetch applications' });
    }
  });

  // Apply for a job
  app.post('/api/applicant/apply', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const user = await storage.getUser(userId);

      if (!user || user.role !== 'applicant') {
        return res.status(403).json({ message: 'Access denied' });
      }

      const profile = await storage.getApplicant(userId);
      if (!profile) {
        return res.status(404).json({ message: 'Profile not found' });
      }

      const { jobId } = req.body;

      // Check if already applied
      const existingApplications = await storage.getApplications({ 
        applicantId: profile.id, 
        jobId: parseInt(jobId) 
      });

      if (existingApplications.length > 0) {
        return res.status(400).json({ message: 'Already applied for this job' });
      }

      const application = await storage.createApplication({
        jobId: parseInt(jobId),
        applicantId: profile.id,
        status: 'submitted',
        submittedOn: new Date().toISOString().split('T')[0],
        remarks: null,
        interviewDate: null,
        interviewScore: null,
      });

      res.json(application);
    } catch (error) {
      console.error('Error applying for job:', error);
      res.status(500).json({ message: 'Failed to apply for job' });
    }
  });

  // Protected admin routes

  // Get all applications (admin)
  app.get('/api/admin/applications', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.id);
      if (!user || user.role !== 'admin') {
        return res.status(403).json({ message: 'Access denied' });
      }

      const jobId = req.query.jobId ? parseInt(req.query.jobId as string) : undefined;
      const status = req.query.status as string | undefined;

      const applications = await storage.getApplications({ jobId, status });
      res.json(applications);
    } catch (error) {
      console.error('Error fetching applications:', error);
      res.status(500).json({ message: 'Failed to fetch applications' });
    }
  });

  // Create job (admin)
  app.post('/api/admin/jobs', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.id);
      if (!user || user.role !== 'admin') {
        return res.status(403).json({ message: 'Access denied' });
      }

      const jobData = {
        ...req.body,
        createdBy: user.id,
        description: req.body.description || null,
      };

      const job = await storage.createJob(jobData);
      res.json(job);
    } catch (error) {
      console.error('Error creating job:', error);
      res.status(500).json({ message: 'Failed to create job' });
    }
  });

  // Update job (admin)
  app.put('/api/admin/jobs/:id', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.id);
      if (!user || user.role !== 'admin') {
        return res.status(403).json({ message: 'Access denied' });
      }

      const jobId = parseInt(req.params.id);
      const job = await storage.updateJob(jobId, req.body);
      res.json(job);
    } catch (error) {
      console.error('Error updating job:', error);
      res.status(500).json({ message: 'Failed to update job' });
    }
  });

  // Create notice (admin)
  app.post('/api/admin/notices', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.id);
      if (!user || user.role !== 'admin') {
        return res.status(403).json({ message: 'Access denied' });
      }

      const noticeData = {
        ...req.body,
        createdBy: user.id,
        type: req.body.type || null,
      };

      const notice = await storage.createNotice(noticeData);
      res.json(notice);
    } catch (error) {
      console.error('Error creating notice:', error);
      res.status(500).json({ message: 'Failed to create notice' });
    }
  });

  // Protected board committee routes

  // Get applications for review (board)
  app.get('/api/board/applications', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.id);
      if (!user || user.role !== 'board') {
        return res.status(403).json({ message: 'Access denied' });
      }

      const jobId = req.query.jobId ? parseInt(req.query.jobId as string) : undefined;
      const status = req.query.status as string | undefined;

      const applications = await storage.getApplications({ jobId, status });
      res.json(applications);
    } catch (error) {
      console.error('Error fetching applications:', error);
      res.status(500).json({ message: 'Failed to fetch applications' });
    }
  });

  // Update application status (board)
  app.put('/api/board/applications/:id', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.id);
      if (!user || user.role !== 'board') {
        return res.status(403).json({ message: 'Access denied' });
      }

      const applicationId = parseInt(req.params.id);
      const application = await storage.updateApplication(applicationId, req.body);
      res.json(application);
    } catch (error) {
      console.error('Error updating application:', error);
      res.status(500).json({ message: 'Failed to update application' });
    }
  });

  // File upload endpoint
  app.post('/api/upload', isAuthenticated, upload.single('file'), async (req: any, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ message: 'No file uploaded' });
      }

      // In production, you would upload to cloud storage (AWS S3, etc.)
      const fileUrl = `/uploads/${req.file.filename}`;

      res.json({
        filename: req.file.originalname,
        url: fileUrl,
        size: req.file.size,
        mimeType: req.file.mimetype
      });
    } catch (error) {
      console.error('Error uploading file:', error);
      res.status(500).json({ message: 'Failed to upload file' });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  applicants,
  jobs,
  applications,
  counties,
  constituencies,
  wards,
  departments,
  designations,
  awards,
  specializations,
  coursesOffered,
  notices,
  educationRecords,
  employmentHistory,
  referees,
  documents,
  otpVerification,
  employees,
  payroll,
  type User,
  type UpsertUser,
  type Applicant,
  type Job,
  type Application,
  type Notice,
  type County,
  type Constituency,
  type Ward,
  type Department,
  type Designation,
  type Award,
  type Employee,
  type CourseOffered,
  type OtpVerification,
  type InsertEmployee,
  type Payroll
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, like, sql } from "drizzle-orm";

export interface IStorage {
  // User operations (mandatory for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Applicant operations
  getApplicant(userId: string): Promise<Applicant | undefined>;
  createApplicant(applicant: Omit<Applicant, 'id' | 'createdAt' | 'updatedAt'>): Promise<Applicant>;
  updateApplicant(id: number, applicant: Partial<Applicant>): Promise<Applicant>;

  // Job operations
  getJobs(filters?: { isActive?: boolean; departmentId?: number }): Promise<Job[]>;
  getJob(id: number): Promise<Job | undefined>;
  createJob(job: Omit<Job, 'id' | 'createdAt' | 'updatedAt'>): Promise<Job>;
  updateJob(id: number, job: Partial<Job>): Promise<Job>;

  // Application operations
  getApplications(filters?: { applicantId?: number; jobId?: number; status?: string }): Promise<Application[]>;
  createApplication(application: Omit<Application, 'id' | 'createdAt' | 'updatedAt'>): Promise<Application>;
  updateApplication(id: number, application: Partial<Application>): Promise<Application>;

  // Location operations
  getCounties(): Promise<County[]>;
  getConstituenciesByCounty(countyId: number): Promise<Constituency[]>;
  getWardsByConstituency(constituencyId: number): Promise<Ward[]>;

  // System configuration operations
  getDepartments(): Promise<Department[]>;
  getDesignations(): Promise<Designation[]>;
  getAwards(): Promise<Award[]>;
  getCoursesOffered(): Promise<CourseOffered[]>;

  // Notice operations
  getNotices(isPublished?: boolean): Promise<Notice[]>;
  createNotice(notice: Omit<Notice, 'id' | 'createdAt' | 'updatedAt'>): Promise<Notice>;
  updateNotice(id: number, notice: Partial<Notice>): Promise<Notice>;

   // Employee operations
  verifyEmployee(personalNumber: string, idNumber: string): Promise<Payroll | undefined>;
  upsertEmployeeDetails(applicantId: number, employeeData: Partial<InsertEmployee>): Promise<Employee>;


  // OTP operations
  createOtp(phoneNumber: string, otp: string): Promise<OtpVerification>;
  comparePasswords(password: string): Promise<boolean>;
  verifyOtp(phoneNumber: string, otp: string): Promise<boolean>;
  getUserByEmail(email: string): Promise<User>;
  verifyEmail(email: string): Promise<boolean>;
  cleanupExpiredOtps(): Promise<void>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // Applicant operations
  async getApplicant(userId: string): Promise<Applicant | undefined> {
    const [applicant] = await db
      .select()
      .from(applicants)
      .where(eq(applicants.userId, userId));
    return applicant;
  }

  async createApplicant(applicant: Omit<Applicant, 'id' | 'createdAt' | 'updatedAt'>): Promise<Applicant> {
    const [newApplicant] = await db
      .insert(applicants)
      .values(applicant)
      .returning();
    return newApplicant;
  }

  async updateApplicant(id: number, applicant: Partial<Applicant>): Promise<Applicant> {
    const [updatedApplicant] = await db
      .update(applicants)
      .set({ ...applicant, updatedAt: new Date() })
      .where(eq(applicants.id, id))
      .returning();
    return updatedApplicant;
  }

  // Job operations
  async getJobs(filters?: { isActive?: boolean; departmentId?: number }): Promise<Job[]> {
    const conditions = [];

    if (filters?.isActive !== undefined) {
      conditions.push(eq(jobs.isActive, filters.isActive));
    }

    if (filters?.departmentId) {
      conditions.push(eq(jobs.departmentId, filters.departmentId));
    }

    let query = db.select().from(jobs);
    if (conditions.length > 0) {
      query = query.where(and(...conditions));
    }

    return await query.orderBy(desc(jobs.createdAt));
  }

  async getJob(id: number): Promise<Job | undefined> {
    const [job] = await db.select().from(jobs).where(eq(jobs.id, id));
    return job;
  }

  async createJob(job: Omit<Job, 'id' | 'createdAt' | 'updatedAt'>): Promise<Job> {
    const [newJob] = await db.insert(jobs).values(job).returning();
    return newJob;
  }

  async updateJob(id: number, job: Partial<Job>): Promise<Job> {
    const [updatedJob] = await db
      .update(jobs)
      .set({ ...job, updatedAt: new Date() })
      .where(eq(jobs.id, id))
      .returning();
    return updatedJob;
  }

  // Application operations
  async getApplications(filters?: { applicantId?: number; jobId?: number; status?: string }): Promise<Application[]> {
    let query = db.select().from(applications);

    const conditions = [];
    if (filters?.applicantId) {
      conditions.push(eq(applications.applicantId, filters.applicantId));
    }
    if (filters?.jobId) {
      conditions.push(eq(applications.jobId, filters.jobId));
    }
    if (filters?.status) {
      conditions.push(eq(applications.status, filters.status as any));
    }

    if (conditions.length > 0) {
      query = query.where(and(...conditions));
    }

    return await query.orderBy(desc(applications.createdAt));
  }

  async createApplication(application: Omit<Application, 'id' | 'createdAt' | 'updatedAt'>): Promise<Application> {
    const [newApplication] = await db
      .insert(applications)
      .values(application)
      .returning();
    return newApplication;
  }

  async updateApplication(id: number, application: Partial<Application>): Promise<Application> {
    const [updatedApplication] = await db
      .update(applications)
      .set({ ...application, updatedAt: new Date() })
      .where(eq(applications.id, id))
      .returning();
    return updatedApplication;
  }

  // Location operations
  async getCounties(): Promise<County[]> {
    return db.select().from(counties).orderBy(counties.name);
  }

  async getConstituenciesByCounty(countyId: number): Promise<Constituency[]> {
    return db
      .select()
      .from(constituencies)
      .where(eq(constituencies.countyId, countyId))
      .orderBy(constituencies.name);
  }

  async getWardsByConstituency(constituencyId: number): Promise<Ward[]> {
    return db
      .select()
      .from(wards)
      .where(eq(wards.constituencyId, constituencyId))
      .orderBy(wards.name);
  }

  // System configuration operations
  async getDepartments(): Promise<Department[]> {
    return db.select().from(departments).orderBy(departments.name);
  }

  async getDesignations(): Promise<Designation[]> {
    return db.select().from(designations).orderBy(designations.name);
  }

  async getAwards(): Promise<Award[]> {
    return db.select().from(awards).orderBy(awards.name);
  }

  async getCoursesOffered(): Promise<CourseOffered[]> {
    return db.select().from(coursesOffered).orderBy(coursesOffered.name);
  }

  // Notice operations
  async getNotices(isPublished?: boolean): Promise<Notice[]> {
    let query = db.select().from(notices);

    if (isPublished !== undefined) {
      query = query.where(eq(notices.isPublished, isPublished));
    }

    return await query.orderBy(desc(notices.createdAt));
  }

  async createNotice(notice: Omit<Notice, 'id' | 'createdAt' | 'updatedAt'>): Promise<Notice> {
    const [newNotice] = await db.insert(notices).values(notice).returning();
    return newNotice;
  }

  async updateNotice(id: number, notice: Partial<Notice>): Promise<Notice> {
    const [updatedNotice] = await db
      .update(notices)
      .set({ ...notice, updatedAt: new Date() })
      .where(eq(notices.id, id))
      .returning();
    return updatedNotice;
  }
  // OTP operations
  async createOtp(phoneNumber: string, otp: string): Promise<OtpVerification> {
    // Clean up old OTPs for this phone number
    await db.delete(otpVerification).where(eq(otpVerification.phoneNumber, phoneNumber));

    // Create new OTP with 5-minute expiration
    const expiresAt = new Date(Date.now() + 5 * 60 * 1000); // 5 minutes

    const [newOtp] = await db.insert(otpVerification).values({
      phoneNumber,
      otp,
      expiresAt,
      verified: false,
      attempts: 0,
    }).returning();

    return newOtp;
  }

  async verifyEmail(email: string): Promise<boolean> {
    const [emailRet] = await db
      .select()
      .from(users)
      .where(and(eq(users.email, email)))
      .limit(1);
    if (!emailRet) {
      return false;
    }

    return true;
  }
  async comparePasswords(password: string): Promise<boolean> {
    const [passwordRes] = await db
      .select()
      .from(users)
      .where(and(eq(users.passwordHash, password)))
      .limit(1);
    if (!passwordRes) {
      return false;
    }
    return true;
  }
  async getUserByEmail(email: string):Promise<User> {
  const [user] = await db.select().from(users).where(eq(users.email, email));
  return user;
}
  async verifyOtp(phoneNumber: string, otp: string): Promise<boolean> {
    const [otpRecord] = await db
      .select()
      .from(otpVerification)
      .where(
        and(
          eq(otpVerification.phoneNumber, phoneNumber),
          eq(otpVerification.otp, otp),
          eq(otpVerification.verified, false)
        )
      )
      .orderBy(desc(otpVerification.createdAt))
      .limit(1);

    if (!otpRecord) {
      return false;
    }
    // Check if OTP is expired
    if (new Date() > otpRecord.expiresAt) {
      return false;
    }

    // Check if too many attempts
    if ((otpRecord.attempts || 0) >= 3) {
      return false;
    }

    // Increment attempts
    await db
      .update(otpVerification)
      .set({ 
        attempts: (otpRecord.attempts || 0) + 1,
        verified: true 
      })
      .where(eq(otpVerification.id, otpRecord.id));

    return true;
  }

  async cleanupExpiredOtps(): Promise<void> {
    await db
      .delete(otpVerification)
      .where(sql`${otpVerification.expiresAt} < NOW()`);
  }
  // Employee operations
  async verifyEmployee(personalNumber: string, idNumber: string): Promise<Payroll | undefined> {
    const [payrol] = await db
      .select()
      .from(payroll)
      .where(
        and(
          eq(payroll.personalNumber, personalNumber),
          eq(payroll.idNumber, idNumber)
        )
      );
    return payrol;
  }

  async upsertEmployeeDetails(applicantId: number, employeeData: Partial<InsertEmployee>): Promise<Employee> {
    const existingEmployee = await db
      .select()
      .from(employees)
      .where(eq(employees.applicantId, applicantId))
      .then(rows => rows[0]);

    if (existingEmployee) {
      const [updatedEmployee] = await db
        .update(employees)
        .set({ ...employeeData, updatedAt: new Date() })
        .where(eq(employees.id, existingEmployee.id))
        .returning();
      return updatedEmployee;
    } else {
      const [newEmployee] = await db
        .insert(employees)
        .values({
          applicantId,
          ...employeeData,
        } as InsertEmployee)
        .returning();
      return newEmployee;
    }
  }
}

export const storage = new DatabaseStorage();

