            {employmentHistory.map((record, index) => (
              <Card key={index}>
                <CardContent className="p-4">
                  <div className="flex items-center justify-between mb-4">
                    <h5 className="font-medium">Employment Record {index + 1}</h5>
                    {employmentHistory.length > 1 && (
                      <Button
                        type="button"
                        variant="ghost"
