                    <div>
                      <Label>Position</Label>
                      <Input
                        value={record.position}
                        onChange={(e) => updateEmploymentRecord(index, 'position', e.target.value)}
                        placeholder="Job title"
                      />
                    </div>

                    <div>
                      <Label>Start Date</Label>
                      <Input
                        type="date"
