import { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useAuth } from '@/hooks/useAuth';
import Navigation from '@/components/layout/Navigation';
import Sidebar from '@/components/layout/Sidebar';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { isUnauthorizedError } from '@/lib/authUtils';
import { 
  Search, 
  Filter, 
  FileText, 
  CheckCircle, 
  XCircle, 
  Calendar,
  Download,
  Eye,
  GraduationCap,
  Award,
  Briefcase
} from 'lucide-react';

export default function BoardShortlisting() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [searchTerm, setSearchTerm] = useState('');
  const [jobFilter, setJobFilter] = useState('all');
  const [statusFilter, setStatusFilter] = useState('submitted');
  const [selectedApplication, setSelectedApplication] = useState<any>(null);
  const [showRemarks, setShowRemarks] = useState(false);
  const [remarks, setRemarks] = useState('');
  const [score, setScore] = useState('');

  const { data: applications = [], isLoading } = useQuery({
    queryKey: ['/api/board/applications', { status: statusFilter, jobId: jobFilter !== 'all' ? jobFilter : undefined }],
    enabled: !!user && user.role === 'board',
  });

  const { data: jobs = [] } = useQuery({
    queryKey: ['/api/public/jobs'],
  });

  const updateApplicationMutation = useMutation({
    mutationFn: async ({ id, data }: { id: number; data: any }) => {
      return await apiRequest('PUT', `/api/board/applications/${id}`, data);
    },
    onSuccess: () => {
      toast({
        title: 'Application Updated',
        description: 'Application status has been updated successfully.',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/board/applications'] });
      setSelectedApplication(null);
      setShowRemarks(false);
      setRemarks('');
      setScore('');
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: 'Unauthorized',
          description: 'You are logged out. Logging in again...',
          variant: 'destructive',
        });
        setTimeout(() => {
          window.location.href = '/api/login';
        }, 500);
        return;
      }
      toast({
        title: 'Error',
        description: error.message || 'Failed to update application',
        variant: 'destructive',
      });
    },
  });

  const handleStatusUpdate = (applicationId: number, newStatus: string, additionalData: any = {}) => {
    updateApplicationMutation.mutate({
      id: applicationId,
      data: {
        status: newStatus,
        remarks: remarks || additionalData.remarks || '',
        ...additionalData
      }
    });
  };

  const filteredApplications = applications.filter(app => {
    const matchesSearch = 
      app.applicant?.firstName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      app.applicant?.surname?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      app.job?.title?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesJob = jobFilter === 'all' || app.jobId?.toString() === jobFilter;

    return matchesSearch && matchesJob;
  });

  const getQualificationMatch = (application: any) => {
    // Mock qualification matching logic - in real app this would check against job requirements
    const educationLevel = application.applicant?.educationRecords?.length || 0;
    const experience = application.applicant?.employmentHistory?.length || 0;

    if (educationLevel >= 3 && experience >= 2) return { percentage: 100, color: 'text-green-600' };
    if (educationLevel >= 2 && experience >= 1) return { percentage: 85, color: 'text-blue-600' };
    if (educationLevel >= 1) return { percentage: 70, color: 'text-yellow-600' };
    return { percentage: 50, color: 'text-red-600' };
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'submitted':
        return 'bg-blue-100 text-blue-800';
      case 'shortlisted':
        return 'bg-green-100 text-green-800';
      case 'rejected':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-neutral-50">
        <Navigation />
        <div className="flex">
          <Sidebar userRole="board" />
          <main className="flex-1 p-6">
            <div className="animate-pulse space-y-6">
              <div className="h-8 bg-gray-200 rounded w-1/3"></div>
              <div className="h-64 bg-gray-200 rounded"></div>
            </div>
          </main>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-neutral-50">
      <Navigation />

      <div className="flex">
        <Sidebar userRole="board" />

        <main className="flex-1 p-6">
          <div className="max-w-7xl mx-auto">
            {/* Header */}
            <div className="flex items-center justify-between mb-8">
              <div>
                <h1 className="text-3xl font-bold text-gray-900 mb-2">Shortlisting Panel</h1>
                <p className="text-gray-600">
                  Review applications and select candidates for interviews.
                </p>
              </div>

              <div className="flex space-x-2">
                <Button variant="outline">
                  <Download className="w-4 h-4 mr-2" />
                  Export List
                </Button>
                <Button>
                  <FileText className="w-4 h-4 mr-2" />
                  Print Sheet
                </Button>
              </div>
            </div>

            {/* Filters */}
            <Card className="mb-8">
              <CardContent className="p-6">
                <div className="flex flex-col lg:flex-row gap-4">
                  <div className="flex-1">
                    <div className="relative">
                      <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                      <Input
                        placeholder="Search by applicant name or job title..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="pl-10"
                      />
                    </div>
                  </div>

                  <div className="flex gap-4">
                    <Select value={jobFilter} onValueChange={setJobFilter}>
                      <SelectTrigger className="w-48">
                        <SelectValue placeholder="All Jobs" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="all">All Jobs</SelectItem>
                        {jobs.map((job) => (
                          <SelectItem key={job.id} value={job.id.toString()}>
                            {job.title}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>

                    <Select value={statusFilter} onValueChange={setStatusFilter}>
                      <SelectTrigger className="w-40">
                        <SelectValue placeholder="Status" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="submitted">Submitted</SelectItem>
                        <SelectItem value="shortlisted">Shortlisted</SelectItem>
                        <SelectItem value="rejected">Rejected</SelectItem>
                      </SelectContent>
                    </Select>

                    <Button variant="outline">
                      <Filter className="w-4 h-4 mr-2" />
                      More Filters
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Applications Table */}
            <Card>
              <CardHeader>
                <CardTitle>Applications for Review ({filteredApplications.length})</CardTitle>
              </CardHeader>
              <CardContent>
                {filteredApplications.length === 0 ? (
                  <div className="text-center py-12">
                    <FileText className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                    <h3 className="text-xl font-semibold text-gray-900 mb-2">No Applications Found</h3>
                    <p className="text-gray-600">
                      No applications match your current filters.
                    </p>
                  </div>
                ) : (
                  <div className="overflow-x-auto">
                    <table className="w-full">
                      <thead>
                        <tr className="border-b border-gray-200">
                          <th className="text-left py-3 px-4 font-medium text-gray-600">Applicant</th>
                          <th className="text-left py-3 px-4 font-medium text-gray-600">Qualification Match</th>
                          <th className="text-left py-3 px-4 font-medium text-gray-600">Documents</th>
                          <th className="text-left py-3 px-4 font-medium text-gray-600">Score</th>
                          <th className="text-left py-3 px-4 font-medium text-gray-600">Status</th>
                          <th className="text-left py-3 px-4 font-medium text-gray-600">Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {filteredApplications.map((application) => {
                          const qualMatch = getQualificationMatch(application);

                          return (
                            <tr key={application.id} className="border-b border-gray-100 hover:bg-gray-50">
                              <td className="py-3 px-4">
                                <div className="flex items-center space-x-3">
                                  <div className="w-10 h-10 bg-primary rounded-full flex items-center justify-center text-white text-sm font-medium">
                                    {application.applicant?.firstName?.[0] || 'A'}
                                    {application.applicant?.surname?.[0] || ''}
                                  </div>
                                  <div>
                                    <div className="font-medium text-gray-900">
                                      {application.applicant?.firstName} {application.applicant?.surname}
                                    </div>
                                    <div className="text-sm text-gray-600 flex items-center">
                                      <GraduationCap className="w-3 h-3 mr-1" />
                                      {application.job?.title}
                                    </div>
                                  </div>
                                </div>
                              </td>
                              <td className="py-3 px-4">
                                <div className="flex items-center space-x-2">
                                  <div className={`w-3 h-3 rounded-full ${qualMatch.percentage >= 85 ? 'bg-green-500' : qualMatch.percentage >= 70 ? 'bg-yellow-500' : 'bg-red-500'}`}></div>
                                  <span className={`text-sm font-medium ${qualMatch.color}`}>
                                    {qualMatch.percentage}% Match
                                  </span>
                                </div>
                                <div className="text-xs text-gray-500 mt-1">
                                  Education, Experience verified
                                </div>
                              </td>
                              <td className="py-3 px-4">
                                <Button 
                                  size="sm" 
                                  variant="outline"
                                  className="bg-blue-50 text-blue-700 hover:bg-blue-100"
                                >
                                  <FileText className="w-3 h-3 mr-1" />
                                  View PDF
                                </Button>
                              </td>
                              <td className="py-3 px-4">
                                <Input 
                                  type="number" 
                                  placeholder="0-100" 
                                  max="100"
                                  className="w-20 text-sm"
                                  value={score}
                                  onChange={(e) => setScore(e.target.value)}
                                />
                              </td>
                              <td className="py-3 px-4">
                                <Badge className={getStatusColor(application.status)}>
                                  {application.status?.charAt(0).toUpperCase() + application.status?.slice(1)}
                                </Badge>
                              </td>
                              <td className="py-3 px-4">
                                <div className="flex space-x-2">
                                  <Dialog>
                                    <DialogTrigger asChild>
                                      <Button 
                                        size="sm" 
                                        className="bg-secondary hover:bg-green-700"
                                        onClick={() => setSelectedApplication(application)}
                                      >
                                        <CheckCircle className="w-3 h-3 mr-1" />
                                        Shortlist
                                      </Button>
                                    </DialogTrigger>
                                    <DialogContent>
                                      <DialogHeader>
                                        <DialogTitle>Shortlist Candidate</DialogTitle>
                                      </DialogHeader>
                                      <div className="space-y-4">
                                        <div>
                                          <Label>Interview Date</Label>
                                          <Input type="date" className="mt-1" />
                                        </div>
                                        <div>
                                          <Label>Remarks</Label>
                                          <Textarea 
                                            placeholder="Add remarks about the candidate..."
                                            value={remarks}
                                            onChange={(e) => setRemarks(e.target.value)}
                                            className="mt-1"
                                          />
                                        </div>
                                        <div className="flex justify-end space-x-2">
                                          <Button variant="outline">Cancel</Button>
                                          <Button 
                                            onClick={() => handleStatusUpdate(selectedApplication?.id, 'shortlisted', { interviewDate: new Date().toISOString().split('T')[0] })}
                                            disabled={updateApplicationMutation.isPending}
                                          >
                                            {updateApplicationMutation.isPending ? 'Processing...' : 'Shortlist'}
                                          </Button>
                                        </div>
                                      </div>
                                    </DialogContent>
                                  </Dialog>

                                  <Button 
                                    size="sm" 
                                    variant="outline"
                                    onClick={() => {
                                      setSelectedApplication(application);
                                      setShowRemarks(true);
                                    }}
                                  >
                                    <Eye className="w-3 h-3 mr-1" />
                                    View Profile
                                  </Button>

                                  <Button 
                                    size="sm" 
                                    variant="outline"
                                    className="text-red-600 hover:bg-red-50"
                                    onClick={() => handleStatusUpdate(application.id, 'rejected', { remarks: 'Did not meet requirements' })}
                                  >
                                    <XCircle className="w-3 h-3 mr-1" />
                                    Reject
                                  </Button>
                                </div>
                              </td>
                            </tr>
                          );
                        })}
                      </tbody>
                    </table>
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Profile View Dialog */}
            <Dialog open={showRemarks} onOpenChange={setShowRemarks}>
              <DialogContent className="max-w-4xl">
                <DialogHeader>
                  <DialogTitle>
                    Candidate Profile - {selectedApplication?.applicant?.firstName} {selectedApplication?.applicant?.surname}
                  </DialogTitle>
                </DialogHeader>

                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                  <div>
                    <h4 className="font-semibold mb-3 flex items-center">
                      <Award className="w-4 h-4 mr-2" />
                      Qualifications
                    </h4>
                    <div className="space-y-2 text-sm">
                      <div className="p-3 bg-gray-50 rounded-lg">
                        <p className="font-medium">Education</p>
                        <p className="text-gray-600">Bachelor's in Information Technology</p>
                        <p className="text-gray-500 text-xs">University of Nairobi, 2020</p>
                      </div>
                      <div className="p-3 bg-gray-50 rounded-lg">
                        <p className="font-medium">Professional Certification</p>
                        <p className="text-gray-600">CISCO Certified Network Associate</p>
                        <p className="text-gray-500 text-xs">Valid until 2025</p>
                      </div>
                    </div>
                  </div>

                  <div>
                    <h4 className="font-semibold mb-3 flex items-center">
                      <Briefcase className="w-4 h-4 mr-2" />
                      Experience
                    </h4>
                    <div className="space-y-2 text-sm">
                      <div className="p-3 bg-gray-50 rounded-lg">
                        <p className="font-medium">IT Support Specialist</p>
                        <p className="text-gray-600">TechCorp Ltd</p>
                        <p className="text-gray-500 text-xs">2021 - Present (3 years)</p>
                      </div>
                      <div className="p-3 bg-gray-50 rounded-lg">
                        <p className="font-medium">Junior Developer</p>
                        <p className="text-gray-600">StartupXYZ</p>
                        <p className="text-gray-500 text-xs">2020 - 2021 (1 year)</p>
                      </div>
                    </div>
                  </div>
                </div>

                <div className="mt-6">
                  <h4 className="font-semibold mb-3">Assessment & Remarks</h4>
                  <Textarea 
                    placeholder="Add your assessment and remarks about this candidate..."
                    value={remarks}
                    onChange={(e) => setRemarks(e.target.value)}
                    rows={4}
                  />
                </div>

                <div className="flex justify-end space-x-2 mt-6">
                  <Button variant="outline" onClick={() => setShowRemarks(false)}>
                    Close
                  </Button>
                  <Button 
                    onClick={() => handleStatusUpdate(selectedApplication?.id, 'shortlisted')}
                    disabled={updateApplicationMutation.isPending}
                  >
                    Shortlist Candidate
                  </Button>
                </div>
              </DialogContent>
            </Dialog>
          </div>
        </main>
      </div>
    </div>
  );
}

client/src/pages/modals/Jobs.tsx.content:
import React, { useEffect } from "react";
import {
  Modal,
  Form,
  Input,
  Select,
  DatePicker,
  Row,
  Col,
  Button,
} from "antd";
import { Department, Award, StudyArea, JobGroup, Job } from "@/types/jobTypes";
import { useForm, Controller } from "react-hook-form";
import { z } from "zod";
import { zodResolver } from "@hookform/resolvers/zod";

const { Option } = Select;

// Validation schema using zod
const jobSchema = z.object({
  code: z.string().min(1, "Advert number is required"),
  start_date: z.date({ required_error: "Start date is required" }),
  end_date: z.date({ required_error: "End date is required" }),
  award_id: z.string().min(1, "Certificate is required"),
  required_courses: z.string().min(1, "Course selection is required"),
  title: z.string().min(1, "Job title is required"),
  grade: z.string().min(1, "Job group is required"),
  posts: z.coerce.number().min(1, "Number of posts is required"),
  department_id: z.string().min(1, "Department is required"),
  category: z.string().min(1, "Status is required"),
  experience: z.coerce.number().min(0, "Experience is required"),
  description: z.string().min(1, "Description is required"),
  advert_type: z.string().min(1, "Advert type is required"),
  required_certificate: z.string().optional(),
});

type JobFormValues = z.infer<typeof jobSchema>;

interface JobModalProps {
  isOpen: boolean;
  isEdit: boolean;
  closeModal: () => void;
  onSubmit: (values: JobFormValues) => void;
  initialValues?: Partial<Job>;
  departments: Department[];
  awards: Award[];
  studyArea: StudyArea[];
  jg: JobGroup[];
}

export const JobModal: React.FC<JobModalProps> = ({
  isOpen,
  isEdit,
  closeModal,
  onSubmit,
  initialValues,
  departments,
  awards,
  studyArea,
  jg,
}) => {
  const [form] = Form.useForm();

  const {
    control,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm<JobFormValues>({
    resolver: zodResolver(jobSchema),
    defaultValues: initialValues,
  });

  useEffect(() => {
    if (isEdit && initialValues) {
      reset(initialValues as any);
      form.setFieldsValue(initialValues);
    } else {
      reset({} as any);
      form.resetFields();
    }
  }, [isEdit, initialValues, form, reset]);

  const handleOk = handleSubmit((values) => {
    onSubmit(values);
    closeModal();
  });

  return (
    <Modal
      className="job-modal-header-green"
      title={isEdit ? "Edit and Update Adverts" : "Job Advertisements"}
      open={isOpen}
      onCancel={() => {
        form.resetFields();
        closeModal();
      }}
      okText={isEdit ? "Update" : "Save"}
      width="90%"
      style={{ maxWidth: "75%" }}
      onOk={handleOk}
      centered
      destroyOnClose
    >
      <Form form={form} layout="vertical">
        <Row gutter={16}>
          <Col xs={24} sm={4}>
            <Form.Item
              name="code"
              label="Advert #"
              rules={[{ required: true, message: "Advert number is required" }]}
            >
              <Input placeholder="Enter Advert Number" />
            </Form.Item>
          </Col>
          <Col xs={24} sm={4}>
            <Form.Item
              name="start_date"
              label="Start Date"
              rules={[{ required: true, message: "Start date is required" }]}
            >
              <DatePicker style={{ width: "100%" }} />
            </Form.Item>
          </Col>
          <Col xs={24} sm={4}>
            <Form.Item
              name="end_date"
              label="End Date"
              rules={[{ required: true, message: "End date is required" }]}
            >
              <DatePicker style={{ width: "100%" }} />
            </Form.Item>
          </Col>
          <Col xs={24} sm={4}>
            <Form.Item
              name="award_id"
              label="Certificate"
              rules={[{ required: true, message: "Certificate is required" }]}
            >
              <Select>
                {awards.map((a) => (
                  <Option key={a.award_id} value={a.award_id}>
                    {a.name}
                  </Option>
                ))}
              </Select>
            </Form.Item>
          </Col>
          <Col xs={24} sm={8}>
            <Form.Item
              name="required_courses"
              label="Courses"
              rules={[{ required: true, message: "Course selection is required" }]}
            >
              <Select placeholder="Select required courses">
                {studyArea.map((course) => (
                  <Option key={course.area_id} value={course.area_id}>
                    {course.name}
                  </Option>
                ))}
              </Select>
            </Form.Item>
          </Col>
        </Row>
        <Row gutter={16}>
          <Col xs={24} sm={16}>
            <Form.Item
              name="title"
              label="Job Title"
              rules={[{ required: true, message: "Job title is required" }]}
            >
              <Input placeholder="Enter job title" />
            </Form.Item>
          </Col>
          <Col xs={24} sm={4}>
            <Form.Item
              name="grade"
              label="JG/ Scale"
              rules={[{ required: true, message: "Job group is required" }]}
            >
              <Select>
                {jg.map((j) => (
                  <Option key={j.jg_id} value={j.jg_id}>
                    {j.name}
                  </Option>
                ))}
              </Select>
            </Form.Item>
          </Col>
          <Col xs={24} sm={4}>
            <Form.Item
              name="posts"
              label="Posts"
              rules={[{ required: true, message: "Number of posts is required" }]}
            >
              <Input type="number" placeholder="Enter number of posts" />
            </Form.Item>
          </Col>
        </Row>
        <Row gutter={16}>
          <Col xs={24} sm={16}>
            <Form.Item
              name="department_id"
              label="Department"
              rules={[{ required: true, message: "Department is required" }]}
            >
              <Select placeholder="Select department">
                {departments.map((dept) => (
                  <Option key={dept.department_id} value={dept.department_id}>
                    {dept.name}
                  </Option>
                ))}
              </Select>
            </Form.Item>
          </Col>
          <Col xs={24} sm={4}>
            <Form.Item
              name="category"
              label="Status"
              rules={[{ required: true, message: "Status is required" }]}
            >
              <Select placeholder="Select status">
                <Option value="open">Open</Option>
                <Option value="internal">Internal</Option>
              </Select>
            </Form.Item>
          </Col>
          <Col xs={24} sm={4}>
            <Form.Item
              name="experience"
              label="Experience"
              rules={[{ required: true, message: "Experience is required" }]}
            >
              <Input placeholder="Number of Experience" type="number" />
            </Form.Item>
          </Col>
        </Row>
        <Form.Item
          name="description"
          label="Description"
          rules={[{ required: true, message: "Description is required" }]}
        >
          <Input.TextArea
            placeholder="Enter job description"
            style={{ minHeight: 200 }}
          />
        </Form.Item>
        <Row gutter={16}>
          <Col xs={24} sm={12}>
            <Form.Item
              name="advert_type"
              label="Advert Type"
              rules={[{ required: true, message: "Advert type is required" }]}
            >
              <Select placeholder="Advert Type">
                <Option value="1">First-time Advert</Option>
                <Option value="2">Re-Advertisement</Option>
              </Select>
            </Form.Item>
          </Col>
          <Col xs={24} sm={12}>
            <Form.Item name="required_certificate" label="Required Document (optional)">
              <Select placeholder="Required Document">
                <Option value="1">Letter of First Appointment</Option>
                <Option value="2">Letter of Last Promotion</Option>
              </Select>
            </Form.Item>
          </Col>
        </Row>
      </Form>
    </Modal>
  );
};
