// src/context/AuthContext.tsx
import {
  createContext,
  useContext,
  useState,
  useEffect,
  ReactNode,
} from "react";
import { useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient"; 

type AuthMode = "login" | "signup" | "otp" | "mobile" | null;

interface User {
  id: number;
  name: string;
  email: string;
  role: "admin" | "applicant" | "board" | "accountant" | "guest" | string;
  profilePhoto?: string;
  [key: string]: any;
}

interface NavLink {
  label: string;
  path: string;
  icon?: string;
}

interface AuthContextType {
  open: boolean;
  mode: AuthMode;
  phoneNumber: string;
  user: User | null;
  navLinks: NavLink[];
  openAuth: (mode: AuthMode, phoneNumber?: string) => void;
  closeAuth: () => void;
  changeMode: (mode: AuthMode) => void;
  handleClick: () => void;
  setPhoneNumber: (phone: string) => void;
  refreshUser: () => Promise<User | null>;
  setUser: (user: User | null) => void;
  logout: () => void;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: ReactNode }) {
  const [open, setOpen] = useState(false);
  const [mode, setMode] = useState<AuthMode>(null);
  const [phoneNumber, setPhoneNumber] = useState("");
  const [user, setUser] = useState<User | null>(null);
  const [navLinks, setNavLinks] = useState<NavLink[]>([]);
  const queryClient = useQueryClient();

  // üîÅ Load user + nav links on mount
  useEffect(() => {
    (async () => {
      const data = await refreshUser();
      if (data) updateNavLinks(data.role);
    })();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // ‚úÖ Refresh user info
  const refreshUser = async (): Promise<User | null> => {
    try {
      const data = await apiRequest("GET", "/api/auth/me");
      setUser(data);
      queryClient.setQueryData(["/api/auth/me"], data);
      updateNavLinks(data?.role);
      return data;
    } catch (err: any) {
      if (err?.status === 401) {
        logout();
      } else {
        console.error("Failed to refresh user:", err);
      }
      return null;
    }
  };

  // ‚úÖ Logout handler
  const logout = async () => {
    try {
      await apiRequest("POST", "/api/auth/logout");
    } catch (e) {
      console.warn("Logout error:", e);
    } finally {
      setUser(null);
      setNavLinks([]);
      queryClient.clear();
    }
  };

  // ‚úÖ Dynamic role-based navigation
  const updateNavLinks = (role?: string) => {
    if (!role) {
      setNavLinks([]);
      return;
    }

    const common: NavLink[] = [
      { label: "Home", path: "/", icon: "home" },
      { label: "Profile", path: "/profile", icon: "user" },
    ];

    const admin: NavLink[] = [
      ...common,
      { label: "Dashboard", path: "/admin", icon: "layout-dashboard" },
      { label: "Jobs", path: "/admin/jobs", icon: "briefcase" },
      { label: "Applications", path: "/admin/applications", icon: "file-text" },
      { label: "Reports", path: "/admin/reports", icon: "bar-chart" },
      { label: "Settings", path: "/admin/settings", icon: "settings" },
    ];

    const applicant: NavLink[] = [
      ...common,
      { label: "Dashboard", path: "/dashboard", icon: "layout-dashboard" },
      { label: "Applications", path: "/applications", icon: "file-text" },
      { label: "Documents", path: "/documents", icon: "folder" },
    ];

    const board: NavLink[] = [
      ...common,
      { label: "Dashboard", path: "/board", icon: "layout-dashboard" },
      { label: "Shortlisting", path: "/board/shortlisting", icon: "users" },
      { label: "Interviews", path: "/board/interviews", icon: "clipboard" },
      { label: "Scoring", path: "/board/scoring", icon: "star" },
      { label: "Reports", path: "/board/reports", icon: "bar-chart" },
    ];

    const accountant: NavLink[] = [
      ...common,
      { label: "Dashboard", path: "/accountant", icon: "layout-dashboard" },
      { label: "Payments", path: "/accountant/payments", icon: "credit-card" },
      { label: "Reports", path: "/accountant/reports", icon: "bar-chart" },
    ];

    switch (role.toLowerCase()) {
      case "admin":
        setNavLinks(admin);
        break;
      case "applicant":
        setNavLinks(applicant);
        break;
      case "board":
        setNavLinks(board);
        break;
      case "accountant":
        setNavLinks(accountant);
        break;
      default:
        setNavLinks(common);
    }
  };

  // ‚úÖ Modal helpers
  const openAuth = (newMode: AuthMode, phone?: string) => {
    setMode(newMode);
    setOpen(true);
    if (phone !== undefined) setPhoneNumber(phone);
  };
  const handleClick = () => openAuth("login");
  const closeAuth = () => {
    setOpen(false);
    setMode(null);
  };
  const changeMode = (newMode: AuthMode) => setMode(newMode);

  return (
    <AuthContext.Provider
      value={{
        open,
        mode,
        phoneNumber,
        user,
        navLinks,
        setUser,
        openAuth,
        closeAuth,
        changeMode,
        handleClick,
        setPhoneNumber,
        refreshUser,
        logout,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
}

export function useAuthContext() {
  const ctx = useContext(AuthContext);
  if (!ctx)
    throw new Error("useAuthContext must be used inside an AuthProvider");
  return ctx;
}

