      XLSX.utils.book_append_sheet(workbook, worksheet, 'Interview Schedule');

      // Set column widths
      worksheet['!cols'] = [
        { wch: 15 }, // ApplicationId
        { wch: 30 }, // JobTitle
        { wch: 25 }, // Name
        { wch: 15 }, // IdNumber
