                                <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center">
                                  {uploadedImageFile ? (
                                    <div className="space-y-3">
                                      {previewImageUrl && (
                                        <img 
                                          src={previewImageUrl} 
                                          alt="Preview" 
                                          className="max-h-32 mx-auto rounded-lg"
                                        />
                                      )}
                                      <div className="flex items-center justify-center space-x-2">
                                        <span className="text-sm text-gray-600">
                                          {uploadedImageFile.name}
                                        </span>
                                        <Button
                                          type="button"
                                          variant="ghost"
                                          size="sm"
                                          onClick={() => {
                                            setUploadedImageFile(null);
                                            setPreviewImageUrl('');
                                          }}
                                          data-testid="button-remove-upload"
                                        >
                                          <X className="w-4 h-4" />
                                        </Button>
                                      </div>
                                    </div>
                                  ) : (
                                    <div className="space-y-2">
                                      <Camera className="w-8 h-8 text-gray-400 mx-auto" />
                                      <div>
                                        <label htmlFor="gallery-file-upload" className="cursor-pointer">
                                          <span className="text-sm text-blue-600 hover:text-blue-500">
                                            Choose an image file
                                          </span>
                                          <input
                                            id="gallery-file-upload"
                                            type="file"
                                            className="hidden"
                                            accept="image/*"
                                            onChange={(e) => {
                                              const file = e.target.files?.[0];
                                              if (file) {
                                                setUploadedImageFile(file);
                                                setPreviewImageUrl(URL.createObjectURL(file));
                                              }
                                            }}
                                            data-testid="input-gallery-file"
                                          />
                                        </label>
                                      </div>
                                      <p className="text-xs text-gray-500">
                                        PNG, JPG up to 10MB
                                      </p>
                                    </div>
                                  )}
                                </div>
                                {galleryFileUpload.state.isUploading && (
                                  <div className="mt-2 text-sm text-blue-600">
                                    Uploading image...
                                  </div>
                                )}
                              </div>
                            )}
                          </div>
                          <div>
                            <Label htmlFor="gallery-eventDate">Event Date</Label>
                            <Input 
                              id="gallery-eventDate" 
                              type="date"
                              {...galleryItemForm.register('eventDate')} 
                            />
                          </div>
                          <div className="flex justify-end space-x-2">
                            <Button type="button" variant="outline" onClick={() => {
                              setIsModalOpen(false);
                              setEditingItem(null);
                              setEditingType('');
                              galleryItemForm.reset();
                              setUploadedImageFile(null);
                              setPreviewImageUrl('');
                            }}>
                              Cancel
                            </Button>
                            <Button type="submit" data-testid="button-submit-gallery">
                              {editingItem && editingType === 'gallery' ? 'Update Gallery Item' : 'Add Gallery Item'}
                            </Button>
                          </div>
                        </form>
                      </DialogContent>
                    </Dialog>
                  </CardHeader>
                  <CardContent>
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                      {galleryItems.length === 0 ? (
                        <div className="col-span-3 text-center py-8 text-gray-500">
                          <Camera className="w-16 h-16 mx-auto mb-4 text-gray-300" />
                          <p>No gallery items yet. Click "Add Gallery Item" to get started.</p>
                        </div>
                      ) : (
                        (galleryItems as any[]).map((item: any) => (
                          <Card key={item.id} className="overflow-hidden">
                            <div className="aspect-video bg-gray-100 relative">
                              {item.imageUrl ? (
                                <img 
                                  src={item.imageUrl} 
                                  alt={item.title}
                                  className="w-full h-full object-cover"
                                />
                              ) : (
                                <div className="w-full h-full flex items-center justify-center">
                                  <Camera className="w-12 h-12 text-gray-400" />
                                </div>
                              )}
                              <div className="absolute top-2 right-2">
                                <Badge variant="secondary">{item.category}</Badge>
                              </div>
                            </div>
                            <CardContent className="p-4">
                              <h4 className="font-semibold mb-2">{item.title}</h4>
                              <p className="text-sm text-gray-600 mb-3 line-clamp-2">{item.description}</p>
                              <div className="flex items-center justify-between">
                                <p className="text-xs text-gray-500">
                                  {item.eventDate ? new Date(item.eventDate).toLocaleDateString() : 'No date'}
                                </p>
                                <div className="flex space-x-2">
                                  <Button 
                                    variant="ghost" 
                                    size="sm"
                                    onClick={() => {
                                      // Set editing mode for gallery item
                                      setEditingItem(item);
                                      setEditingType('gallery');
                                      galleryItemForm.reset({
                                        title: item.title,
                                        description: item.description || '',
                                        category: item.category,
                                        imageUrl: item.imageUrl || '',
                                        eventDate: item.eventDate || ''
                                      });
                                      setPreviewImageUrl(item.imageUrl || '');
                                      setIsModalOpen(true);
                                    }}
                                  >
                                    <Edit className="w-4 h-4" />
                                  </Button>
                                  <Button 
                                    variant="ghost" 
                                    size="sm"
                                    onClick={() => handleDeleteItem(item.id, '/api/admin/gallery', 'gallery item')}
                                  >
                                    <Trash2 className="w-4 h-4" />
                                  </Button>
                                </div>
                              </div>
                            </CardContent>
                          </Card>
                        ))
                      )}
                    </div>
                  </CardContent>
                </Card>
              </TabsContent>

              {/* System Settings Tab */}
              <TabsContent value="system">
                <Card>
                  <CardHeader>
                    <div>
                      <CardTitle>System Settings</CardTitle>
                      <p className="text-sm text-gray-600 mt-1">Manage system-wide settings including favicon</p>
                    </div>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-6">
                      {/* Favicon Management */}
                      <div className="p-4 border rounded-lg">
                        <h3 className="text-lg font-semibold mb-4">Favicon Management</h3>
                        <p className="text-sm text-gray-600 mb-4">
                          Upload a favicon for your website. Recommended size: 16x16 or 32x32 pixels. Accepted formats: .ico, .png
                        </p>
                        <div className="space-y-4">
                          <div className="flex items-center space-x-4">
                            <div className="flex-1">
                              <Label htmlFor="favicon-upload">Upload Favicon</Label>
                              <Input 
                                id="favicon-upload"
                                type="file"
                                accept=".ico,.png"
                                onChange={(e) => {
                                  const file = e.target.files?.[0];
                                  if (file) {
                                    // Validate file type
                                    const validTypes = ['image/x-icon', 'image/vnd.microsoft.icon', 'image/png'];
                                    if (!validTypes.includes(file.type) && !file.name.endsWith('.ico')) {
                                      toast({
                                        title: 'Error',
                                        description: 'Invalid file type. Please upload a .ico or .png file.',
                                        variant: 'destructive',
                                      });
                                      return;
                                    }

                                    // Create FormData and upload
                                    const formData = new FormData();
                                    formData.append('file', file);

                                    // Upload using dedicated favicon endpoint
                                    fetch('/api/admin/upload-favicon', {
                                      method: 'POST',
                                      body: formData
                                    })
                                    .then(response => response.json())
                                    .then(data => {
                                      if (data.success) {
                                        toast({
                                          title: 'Success',
                                          description: 'Favicon uploaded successfully! Please refresh the page to see changes.',
                                        });
                                        // Reset the file input
                                        (e.target as HTMLInputElement).value = '';
                                      } else {
                                        toast({
                                          title: 'Error',
                                          description: data.message || 'Failed to upload favicon',
                                          variant: 'destructive',
                                        });
                                      }
                                    })
                                    .catch(error => {
                                      toast({
                                        title: 'Error',
                                        description: 'Failed to upload favicon. Please try again.',
                                        variant: 'destructive',
                                      });
                                    });
                                  }
                                }}
                                data-testid="input-favicon-upload"
                              />
                            </div>
                          </div>

                          <div className="flex items-center space-x-4 p-3 bg-gray-50 rounded-lg">
                            <div className="w-8 h-8 bg-white border rounded flex items-center justify-center">
                              <img 
                                src="/uploads/favicon.ico" 
                                alt="Current favicon"
                                className="w-4 h-4"
                                onError={(e) => {
                                  (e.currentTarget as HTMLElement).style.display = 'none';
                                  ((e.currentTarget.nextElementSibling) as HTMLElement).style.display = 'block';
                                }}
                              />
                              <div style={{display: 'none'}} className="text-xs text-gray-400">üèõÔ∏è</div>
                            </div>
                            <div>
                              <p className="text-sm font-medium">Current Favicon</p>
                              <p className="text-xs text-gray-500">Will fallback to default if not found</p>
                            </div>
                          </div>
                        </div>
                      </div>

                      {/* Future system settings can be added here */}
                      <div className="p-4 border rounded-lg border-dashed border-gray-300">
                        <h3 className="text-lg font-semibold mb-2 text-gray-400">Future System Settings</h3>
                        <p className="text-sm text-gray-500">
                          Additional system settings like site title, default language, 
                          email configurations, etc. will be added here.
                        </p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              </TabsContent>
              {/* Carousel Tab */}
              <TabsContent value="carousel">
                <Card>
                  <CardHeader className="flex flex-row items-center justify-between">
                    <div>
                      <CardTitle>Carousel Management</CardTitle>
                      <p className="text-sm text-gray-600 mt-1">
                        Manage homepage carousel slides
                      </p>
                    </div>
                    <Dialog>
                      <DialogTrigger asChild>
                        <Button
                          onClick={() => {
                            setEditingCarouselSlide(null);
                            carouselSlideForm.reset();
                            setCarouselImageMode("gradient");
                            setCarouselImageFile(null);
                            setCarouselMobileImageFile(null);
                            setCarouselImagePreview("");
                            setCarouselMobilePreview("");
                          }}
                        >
                          <Plus className="w-4 h-4 mr-2" />
                          Add Slide
                        </Button>
                      </DialogTrigger>
                      <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
                        <DialogHeader>
                          <DialogTitle>
                            {editingCarouselSlide ? "Edit Slide" : "Add New Slide"}
                          </DialogTitle>
                        </DialogHeader>
                        <form
                          onSubmit={carouselSlideForm.handleSubmit(handleCreateCarouselSlide)}
                          className="space-y-4"
                        >
                          <div>
                            <Label htmlFor="slide-title">Title</Label>
                            <Input id="slide-title" {...carouselSlideForm.register("title")} />
                          </div>
                          <div>
                            <Label htmlFor="slide-subtitle">Subtitle</Label>
                            <Input
                              id="slide-subtitle"
                              {...carouselSlideForm.register("subtitle")}
                            />
                          </div>
                          <div>
                            <Label htmlFor="slide-icon">Icon</Label>
                            <Select
                              onValueChange={(v) => carouselSlideForm.setValue("iconName", v as any)}
                              defaultValue={editingCarouselSlide?.iconName}
                            >
                              <SelectTrigger>
                                <SelectValue placeholder="Select an icon" />
                              </SelectTrigger>
                              <SelectContent>
                                <SelectItem value="Building">Building</SelectItem>
                                <SelectItem value="GraduationCap">Graduation Cap</SelectItem>
                                <SelectItem value="Users">Users</SelectItem>
                                <SelectItem value="Award">Award</SelectItem>
                              </SelectContent>
                            </Select>
                          </div>

                          {/* Image vs Gradient mode toggle */}
                          <div>
                            <Label>Background Type</Label>
                            <div className="flex gap-4 mt-1">
                              <Button
                                type="button"
                                variant={carouselImageMode === "gradient" ? "default" : "outline"}
                                onClick={() => setCarouselImageMode("gradient")}
                              >
                                Gradient
                              </Button>
                              <Button
                                type="button"
                                variant={carouselImageMode === "image" ? "default" : "outline"}
                                onClick={() => setCarouselImageMode("image")}
                              >
                                Image
                              </Button>
                            </div>
                          </div>

                          {carouselImageMode === "gradient" ? (
                            <div className="space-y-2">
                              <Label>Gradient Colors</Label>
                              <div className="flex gap-2 items-center">
                                <Input
                                  type="color"
                                  title="Start Color"
                                  className="w-10 h-10 p-0 border-none bg-transparent"
                                  {...carouselSlideForm.register('gradientStart')}
                                  defaultValue="#3b82f6"
                                />
                                <span>to</span>
                                <Input
                                  type="color"
                                  title="End Color"
                                  className="w-10 h-10 p-0 border-none bg-transparent"
                                  {...carouselSlideForm.register('gradientEnd')}
                                  defaultValue="#a21caf"
                                />
                                <Select
                                  onValueChange={v => carouselSlideForm.setValue('gradientDir', v)}
                                  defaultValue={carouselSlideForm.watch('gradientDir') || 'r'}
                                >
                                  <SelectTrigger title="Gradient Direction">
                                    <SelectValue placeholder="Direction" />
                                  </SelectTrigger>
                                  <SelectContent>
                                    <SelectItem value="r">Right</SelectItem>
                                    <SelectItem value="l">Left</SelectItem>
                                    <SelectItem value="t">Top</SelectItem>
                                    <SelectItem value="b">Bottom</SelectItem>
                                  </SelectContent>
                                </Select>
                              </div>
                              {/* Compose Tailwind gradient string */}
                              <Input
                                id="bg-gradient"
                                placeholder="Tailwind gradient classes"
                                value={`from-[${carouselSlideForm.watch('gradientStart')||'#3b82f6'}] to-[${carouselSlideForm.watch('gradientEnd')||'#a21caf'}] bg-gradient-to-${carouselSlideForm.watch('gradientDir')||'r'}`}
                                readOnly
                              />
                              <Label htmlFor="accent-color">Accent Color</Label>
                              <Input
                                id="accent-color"
                                type="color"
                                className="w-10 h-10 p-0 border-none bg-transparent"
                                {...carouselSlideForm.register("accentColor")}
                              />
                            </div>
                          ) : (
                            <>
                              <div>
                                <Label>Desktop Image</Label>
                                <Input
                                  type="file"
                                  accept="image/*"
                                  onChange={(e) =>
                                    e.target.files?.[0] && setCarouselImageFile(e.target.files[0])
                                  }
                                />
                                {carouselImagePreview && (
                                   <img
                                     src={carouselImagePreview}
                                     alt="Desktop Preview"
                                     className="mt-2 rounded-md h-32 object-cover"
                                   />
                                 )}
                              </div>
                              <div>
                                <Label>Mobile Image</Label>
                                <Input
                                  type="file"
                                  accept="image/*"
                                  onChange={(e) =>
                                    e.target.files?.[0] &&
                                    setCarouselMobileImageFile(e.target.files[0])
                                  }
                                />
                                {carouselMobilePreview && (
                                   <img
                                     src={carouselMobilePreview}
                                     alt="Mobile Preview"
                                     className="mt-2 rounded-md h-32 object-cover"
                                   />
                                 )}
                              </div>
                            </>
                          )}

                          <div className="space-y-2">
                            <Label htmlFor="alt-text">Alt Text (Accessibility)</Label>
                            <Input id="alt-text" {...carouselSlideForm.register("altText")}/>
                            <Label htmlFor="link-href">Link (optional)</Label>
                            <Input id="link-href" {...carouselSlideForm.register("linkHref")}/>
                            <Label htmlFor="cta-label">CTA Label</Label>
                            <Input id="cta-label" {...carouselSlideForm.register("ctaLabel")}/>
                            <Label htmlFor="display-order">Order</Label>
                            <Input id="display-order" type="number" {...carouselSlideForm.register("displayOrder", { valueAsNumber: true })}/>
                            <div className="flex items-center gap-2 mt-2">
                              <Label htmlFor="is-active">Active</Label>
                              <Input id="is-active" type="checkbox" {...carouselSlideForm.register("isActive")}/>
                            </div>
                            <div className="flex justify-end space-x-2 mt-2">
                              <Button type="submit">
                                {editingCarouselSlide ? "Update Slide" : "Create Slide"}
                              </Button>
                            </div>
                          </div>
                        </form>
                      </DialogContent>
                    </Dialog>
                  </CardHeader>

                  <CardContent>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      {carouselSlides.length === 0 ? (
                        <p className="text-gray-500 text-center py-4">
                          No carousel slides yet. Add one above.
                        </p>
                      ) : (
                        carouselSlides.map((slide: any) => (
                          <Card key={slide.id}>
                            <CardContent className="p-4">
                              <h4 className="font-semibold">{slide.title}</h4>
                              <div className="text-xs text-gray-500 mb-1">Order: {slide.displayOrder} | {slide.isActive ? 'Active' : 'Inactive'}</div>
                              <p className="text-sm text-gray-600">{slide.subtitle}</p>
                              {slide.imageUrl ? (
                                <img
                                  src={slide.imageUrl}
                                  alt={slide.altText || slide.title}
                                  className="mt-2 rounded-md h-32 object-cover"
                                />
                              ) : (
                                <div
                                 className="mt-2 h-32 rounded-md"
                                 style={{
                                   background: `linear-gradient(to right, ${slide.bgGradient?.match(/from-\[(.*?)\]/)?.[1]||'#3b82f6'}, ${slide.bgGradient?.match(/to-\[(.*?)\]/)?.[1]||'#a21caf'})`
                                 }}
                                />
                              )}
                               <div className="text-xs mt-2">Accent: <span style={{background: slide.accentColor, color: '#fff', padding: '2px 8px', borderRadius: '4px'}}>{slide.accentColor}</span></div>
                              {slide.linkHref && <div className="text-xs mt-1">Link: <a href={slide.linkHref} className="text-blue-600 underline">{slide.linkHref}</a></div>}
                              {slide.ctaLabel && <div className="text-xs mt-1">CTA: {slide.ctaLabel}</div>}
                              {slide.altText && <div className="text-xs mt-1">Alt: {slide.altText}</div>}
                              <div className="flex gap-2 mt-3">
                                <Button
                                  size="sm"
                                  variant="outline"
                                  onClick={() => {
                                    setEditingCarouselSlide(slide);
                                    carouselSlideForm.reset(slide);
                                    setIsModalOpen(true);
                                  }}
                                >
                                  <Edit className="w-4 h-4 mr-1" /> Edit
                                </Button>
                                <Button
                                  size="sm"
                                  variant="destructive"
                                  onClick={() =>
                                    handleDeleteItem(slide.id, "/api/admin/carousel-slides", "slide")
                                  }
                                >
                                  <Trash2 className="w-4 h-4 mr-1" /> Delete
                                </Button>
                              </div>
                            </CardContent>
                          </Card>
                        ))
                      )}
                    </div>
                  </CardContent>
                </Card>
              </TabsContent>
            </Tabs>
          </div>
        </main>
      </div>
    </div>
  );
}

client/src/index.css.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(20, 14.3%, 4.1%);
  --muted: hsl(60, 4.8%, 95.9%);
  --muted-foreground: hsl(25, 5.3%, 44.7%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(20, 14.3%, 4.1%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(20, 14.3%, 4.1%);
  --border: hsl(20, 5.9%, 90%);
  --input: hsl(20, 5.9%, 90%);
  --primary: hsl(213, 94%, 68%);
  --primary-foreground: hsl(210, 40%, 98%);
  --secondary: hsl(142, 71%, 45%);
  --secondary-foreground: hsl(210, 40%, 98%);
  --accent: hsl(0, 84%, 60%);
  --accent-foreground: hsl(210, 40%, 98%);
  --destructive: hsl(0, 84.2%, 60.2%);
  --destructive-foreground: hsl(60, 9.1%, 97.8%);
  --ring: hsl(20, 14.3%, 4.1%);
  --radius: 0.5rem;
}

.dark {
  --background: hsl(240, 10%, 3.9%);
  --foreground: hsl(0, 0%, 98%);
  --muted: hsl(240, 3.7%, 15.9%);
  --muted-foreground: hsl(240, 5%, 64.9%);
  --popover: hsl(240, 10%, 3.9%);
  --popover-foreground: hsl(0, 0%, 98%);
  --card: hsl(240, 10%, 3.9%);
  --card-foreground: hsl(0, 0%, 98%);
  --border: hsl(240, 3.7%, 15.9%);
  --input: hsl(240, 3.7%, 15.9%);
  --primary: hsl(213, 94%, 68%);
  --primary-foreground: hsl(210, 40%, 98%);
  --secondary: hsl(142, 71%, 45%);
  --secondary-foreground: hsl(210, 40%, 98%);
  --accent: hsl(0, 84%, 60%);
  --accent-foreground: hsl(210, 40%, 98%);
  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(240, 4.9%, 83.9%);
  --radius: 0.5rem;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/* Additional custom styles for the job portal */
.line-clamp-2 {
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

.line-clamp-3 {
  display: -webkit-box;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  @apply bg-gray-100;
}

::-webkit-scrollbar-thumb {
  @apply bg-gray-300 rounded-full;
}

::-webkit-scrollbar-thumb:hover {
  @apply bg-gray-400;
}

/* Animation for drawer */
.drawer-enter {
  transform: translateX(100%);
}

.drawer-enter-active {
  transform: translateX(0);
  transition: transform 300ms ease-out;
}

.drawer-exit {
  transform: translateX(0);
}

.drawer-exit-active {
  transform: translateX(100%);
  transition: transform 300ms ease-out;
}

/* Loading states */
.loading-shimmer {
  background: linear-gradient(90deg, #f0f0f0 25%, #e0e0e0 50%, #f0f0f0 75%);
  background-size: 200% 100%;
  animation: shimmer 1.5s infinite;
}

@keyframes shimmer {
  0% {
    background-position: -200% 0;
  }
  100% {
    background-position: 200% 0;
  }
}

/* Print styles */
@media print {
  .no-print {
    display: none;
  }

  body {
    -webkit-print-color-adjust: exact;
    print-color-adjust: exact;
  }
}

