import "dotenv/config";
import { drizzle } from 'drizzle-orm/node-postgres';
import { Pool } from 'pg';
import * as schema from '@shared/schema';
// import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
// import * as schema from "@shared/schema";

// neonConfig.webSocketConstructor = ws;

// process.env.NODE_TLS_REJECT_UNAUTHORIZED = "0";
if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}
const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle(pool, { schema });

client/src/pages/applicant/Dashboard.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useLocation } from "wouter";
import Navigation from "@/components/layout/Navigation";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { FileText, CheckCircle, Calendar } from "lucide-react";
import ProgressIndicator from "@/components/applicant/ProgressIndicator";
import type { Application } from "@shared/schema";
import { usePublicConfig } from "@/hooks/usePublicConfig";
import { useEffect } from "react";

export default function ApplicantDashboard() {
  const { user } = useAuth();
  const [, navigate] = useLocation();

  const { data: config } = usePublicConfig();
  const jobs = config?.jobs || [];

  // ‚úÖ Fetch applications
  const { data: applications = [] } = useQuery<Application[]>({
    queryKey: ["/api/applicant/applications"],
  });

  // ‚úÖ Fetch applicant profile (progress lives here)
  type ProfileResponse = {
    applicantProfile?: {
      id: number;
      isEmployee?: boolean;
      profileCompletionPercentage?: number;
      completedSteps?: number[];
    };
  };

  const { data: profile } = useQuery<ProfileResponse>({
    queryKey: ["/api/auth/user"],
    enabled: !!user,
  });

  const steps = [
    { id: 1, name: "Personal Details", required: true },
    { id: 1.5, name: "Employee Details", required: true, conditional: true },
    { id: 2, name: "Address Information", required: true },
    { id: 3, name: "Educational Background", required: true },
    { id: 4, name: "Short Courses", required: false },
    { id: 5, name: "Professional Qualifications", required: false },
    { id: 6, name: "Employment History", required: true },
    { id: 7, name: "Referees", required: true },
    { id: 8, name: "Document Uploads", required: true },
  ].map((step) => ({
    ...step,
    completed:
      profile?.applicantProfile?.completedSteps?.includes(step.id as number) ??
      false,
  }));
  // Calculate completion percentage
const completion = profile?.applicantProfile?.profileCompletionPercentage || 0;

// Determine button caption and behavior
const isComplete = completion >= 100;
const buttonLabel = isComplete ? "View Your Profile" : "Continue Profile Setup";

  const getStatusColor = (status: string) => {
    switch (status) {
      case "submitted":
        return "bg-blue-100 text-blue-800";
      case "shortlisted":
        return "bg-green-100 text-green-800";
      case "interviewed":
        return "bg-yellow-100 text-yellow-800";
      case "rejected":
        return "bg-red-100 text-red-800";
      case "hired":
        return "bg-purple-100 text-purple-800";
      default:
        return "bg-gray-100 text-gray-800";
    }
  };
console.log(applications);

  return (
    <div className="min-h-screen bg-neutral-50">
      <Navigation />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">
            Welcome back, {user?.firstName || "Applicant"}!
          </h1>
          <p className="text-gray-600 mt-2">
            Manage your job applications and complete your profile
          </p>
        </div>

        {/* ‚úÖ Profile Completion */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle>Profile Completion</CardTitle>
          </CardHeader>
          <CardContent>
            <ProgressIndicator
              steps={steps}
              currentStep={0}
              completedSteps={profile?.applicantProfile?.completedSteps?.length || 0}
            />
            <div className="mt-6">
              <Button
                onClick={() => {
                if (isComplete) {
                  // ‚úÖ Profile complete ‚Üí View profile at step 1
                  navigate("/profile?step=1");
                  return;
                }
                const completed = profile?.applicantProfile?.completedSteps || [];
                const lastStep = completed.length > 0 ? completed[completed.length - 1] : 1;
                const isEmployeeVerified = profile?.applicantProfile?.isEmployee;

                // ‚úÖ Fix loop at step 1
                let nextStep: number | 1.5 = 1;
                if (lastStep === 1) {
                  nextStep = isEmployeeVerified ? 1.5 : 2;
                } else if (lastStep === 1.5) {
                  nextStep = 2;
                } else if (lastStep < 8) {
                  nextStep = lastStep + 1;
                } else {
                  nextStep = 8; // Max step
                }

                navigate(`/profile?step=${nextStep}`);
              }}
              >
                {buttonLabel}
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Dashboard Stats */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-gray-600 text-sm">Applications Submitted</p>
                  <p className="text-2xl font-bold text-gray-900">
                    {applications.length}
                  </p>
                </div>
                <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center">
                  <FileText className="w-6 h-6 text-primary" />
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-gray-600 text-sm">Shortlisted</p>
                  <p className="text-2xl font-bold text-gray-900">
                    {applications.filter((app) => app.status === "shortlisted").length}
                  </p>
                </div>
                <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center">
                  <CheckCircle className="w-6 h-6 text-secondary" />
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-gray-600 text-sm">Interview Invites</p>
                  <p className="text-2xl font-bold text-gray-900">
                    {applications.filter((app) => app.status === "interviewed").length}
                  </p>
                </div>
                <div className="w-12 h-12 bg-yellow-100 rounded-lg flex items-center justify-center">
                  <Calendar className="w-6 h-6 text-yellow-600" />
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Recent Applications */}
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle>Recent Applications</CardTitle>
              <Button
                variant="outline"
                size="sm"
                onClick={() => navigate("/applications")}
              >
                View All
              </Button>
            </div>
          </CardHeader>
          <CardContent>
            {applications.length === 0 ? (
              <div className="text-center py-8">
                <FileText className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">
                  No applications yet
                </h3>
                <p className="text-gray-600 mb-4">
                  Start browsing and applying for jobs to see them here.
                </p>
                <Button onClick={() => navigate("/jobs")}>Browse Jobs</Button>
              </div>
            ) : (
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead>
                    <tr className="border-b border-gray-200">
                      <th className="text-left py-3 px-4 font-medium text-gray-600">
                        Job Title
                      </th>
                      <th className="text-left py-3 px-4 font-medium text-gray-600">
                        Department
                      </th>
                      <th className="text-left py-3 px-4 font-medium text-gray-600">
                        Applied Date
                      </th>
                      <th className="text-left py-3 px-4 font-medium text-gray-600">
                        Status
                      </th>
                      <th className="text-left py-3 px-4 font-medium text-gray-600">
                        Actions
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    {applications.slice(0, 5).map((application: any) => {
                      return (
                        <tr
                          key={application.id}
                          className="border-b border-gray-100 hover:bg-gray-50"
                        >
                          <td className="py-3 px-4">
                            <div className="font-medium text-gray-900">
                              {application?.job?.title || "‚Äî"}
                            </div>
                            <div className="text-sm text-gray-600">
                              {`Job Group '${application?.job?.jobGroupName}'`}
                            </div>
                          </td>
                          <td className="py-3 px-4">
                            <div className="font-medium text-gray-900">
                              {application?.job?.department?.name || "‚Äî"}
                            </div>
                            <div className="text-sm text-gray-600">                            
                            {application?.interviewDate 
                            ? `Interview Date ${new Date(application.interviewDate).toLocaleDateString()}`
                              : "-"}
                            </div>
                            </td>
                          <td className="py-3 px-4 text-gray-600">
                            {application.submittedOn
                              ? new Date(application.submittedOn).toLocaleDateString()
                              : "-"}
                          </td>
                          <td className="py-3 px-4">
                            <Badge className={getStatusColor(application.status || "")}> 
                              {application.status
                                ? application.status.charAt(0).toUpperCase() +
                                  application.status.slice(1)
                                : "Draft"}
                            </Badge>
                          </td>
                          <td className="py-3 px-4">
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => navigate(`/applications?id=${application.id}`)}
                            >
                              View Details
                            </Button>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

