import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Button } from "@/components/ui/button";
import { Loader2, CheckCircle2, Circle } from "lucide-react";
import { useState, useMemo } from "react";
import { useToast } from "@/hooks/use-toast";
import { useAuthContext } from "@/context/AuthContext";

export default function ProfileSettingsDrawer({ open, onClose, user }: any) {
  const { toast } = useToast();
  const { refreshUser } = useAuthContext();

  const [email, setEmail] = useState(user?.email || "");
  const [password, setPassword] = useState("");
  const [photo, setPhoto] = useState<File | null>(null);
  const [loading, setLoading] = useState(false);

  // ‚úÖ Password strength logic
  const passwordChecks = useMemo(() => {
    return {
      length: password.length >= 8,
      upper: /[A-Z]/.test(password),
      lower: /[a-z]/.test(password),
      number: /[0-9]/.test(password),
      special: /[^A-Za-z0-9]/.test(password),
    };
  }, [password]);

  const isStrong =
    password &&
    Object.values(passwordChecks).every((check) => check === true);

  // ‚úÖ Handle submit
  const handleSubmit = async () => {
    setLoading(true);
    const formData = new FormData();
    formData.append("email", email);
    if (password) formData.append("password", password);
    if (photo) formData.append("profilePhoto", photo);

    try {
      const res = await fetch("/api/users/update-profile", {
        method: "PATCH",
        body: formData,
      });

      if (!res.ok) throw new Error("Failed to update profile");

      toast({
        title: "Profile Updated",
        description: "Your changes have been saved successfully.",
      });

      await refreshUser(); // ‚úÖ Update sidebar avatar + email instantly
      onClose();
    } catch (err) {
      console.error(err);
      toast({
        title: "Error",
        description: "Failed to update profile. Please try again.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle>Account Settings</DialogTitle>
        </DialogHeader>

        <div className="space-y-5 py-2">
          {/* ‚úÖ Profile Photo with Preview */}
          <div className="space-y-2">
            <Label>Profile Photo</Label>

            {photo ? (
              <img
                src={URL.createObjectURL(photo)}
                alt="Preview"
                className="w-24 h-24 rounded-full object-cover border border-gray-200"
              />
            ) : user?.profilePhoto ? (
              <img
                src={user.profilePhoto}
                alt="Current"
                className="w-24 h-24 rounded-full object-cover border border-gray-200"
              />
            ) : (
              <div className="w-24 h-24 flex items-center justify-center rounded-full border border-gray-200 text-gray-400 text-sm">
                No photo
              </div>
            )}

            <input
              type="file"
              accept="image/*"
              className="block w-full text-sm mt-2"
              onChange={(e) => setPhoto(e.target.files?.[0] || null)}
            />
          </div>

          {/* ‚úÖ Email */}
          <div>
            <Label>Email</Label>
            <Input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>

          {/* ‚úÖ Password with Strength Validation */}
          <div className="space-y-2">
            <Label>New Password</Label>
            <Input
              type="password"
              placeholder="Enter new password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />

            {password && (
              <div className="text-sm mt-2 space-y-1">
                <p className="font-medium text-gray-700">
                  Password must include:
                </p>
                <ul className="space-y-1 text-gray-600">
                  {[
                    { key: "length", label: "At least 8 characters" },
                    { key: "upper", label: "An uppercase letter (A‚ÄìZ)" },
                    { key: "lower", label: "A lowercase letter (a‚Äìz)" },
                    { key: "number", label: "A number (0‚Äì9)" },
                    { key: "special", label: "A special symbol (!@#$%)" },
                  ].map(({ key, label }) => (
                    <li key={key} className="flex items-center gap-2">
                      {passwordChecks[key as keyof typeof passwordChecks] ? (
                        <CheckCircle2 className="w-4 h-4 text-green-500" />
                      ) : (
                        <Circle className="w-4 h-4 text-gray-300" />
                      )}
                      <span>{label}</span>
                    </li>
                  ))}
                </ul>
                {isStrong && (
                  <p className="text-green-600 font-medium mt-1">
                    ‚úÖ Strong password!
                  </p>
                )}
              </div>
            )}
          </div>
        </div>

        <DialogFooter>
          <Button variant="outline" onClick={onClose} disabled={loading}>
            Cancel
          </Button>
          <Button onClick={handleSubmit} disabled={loading}>
            {loading ? (
              <>
                <Loader2 className="w-4 h-4 mr-2 animate-spin" /> Saving...
              </>
            ) : (
              "Save Changes"
            )}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

client/src/lib/sanitizeDates.ts.content:
/**
 * Convert form values into a proper Date or null.
 * Handles:
 *  - JS Date objects
 *  - "YYYY-MM-DD" strings from <input type="date">
 *  - numeric years (e.g., 2021 ‚Üí Jan 1st of that year)
 *  - other strings (tries to parse)
 * Returns `null` if invalid.
 */
export function sanitizeDate(value: any): Date | null {
  if (value === null || value === undefined || value === "") {
    return null;
  }

  // Already a Date
  if (value instanceof Date) {
    return isNaN(value.getTime()) ? null : value;
  }

  // Numeric year (e.g., 2021 ‚Üí Jan 1, 2021)
  if (typeof value === "number") {
    const parsed = new Date(value, 0, 1);
    return isNaN(parsed.getTime()) ? null : parsed;
  }

  // YYYY-MM-DD string (from <input type="date">)
  if (typeof value === "string" && /^\d{4}-\d{2}-\d{2}$/.test(value)) {
    const parsed = new Date(`${value}T00:00:00Z`);
    return isNaN(parsed.getTime()) ? null : parsed;
  }

  // Try parsing anything else (e.g., ISO string)
  const parsed = new Date(value);
  return isNaN(parsed.getTime()) ? null : parsed;
}
export const filterEmptyFields = (obj: any): any => {
  if (Array.isArray(obj)) {
    return obj.map(filterEmptyFields).filter((v) => v !== null && v !== undefined);
  }

  if (obj instanceof Date) {
    return obj; // ‚úÖ keep real Date objects
  }

  if (typeof obj === "string") {
    // ‚úÖ keep ISO date strings (YYYY-MM-DD...)
    if (/^\d{4}-\d{2}-\d{2}T/.test(obj)) return obj;
    if (obj.trim() === "") return null;
    return obj;
  }

  if (obj && typeof obj === "object") {
    return Object.fromEntries(
      Object.entries(obj)
        .map(([k, v]) => [k, filterEmptyFields(v)])
        .filter(([_, v]) => v !== null && v !== undefined && v !== "")
    );
  }

  if (obj === null || obj === undefined) return null;

  return obj; // ‚úÖ keep numbers, booleans, etc.
};
type AnyRecord = Record<string, any>;

interface NormalizeOptions {
  dateFields?: string[];
  protectCreatedAt?: boolean;
  setUpdatedAt?: boolean;
}

export function normalizeDates<T extends Record<string, any>>(
  input: T,
  options: NormalizeOptions = {}
): T {
  const {
    dateFields = [],
    protectCreatedAt = true,
    setUpdatedAt = true,
  } = options;

  const output: Record<string, any> = { ...input };

  // ‚úÖ Convert provided fields into Dates
  for (const field of dateFields) {
    if (field in output && output[field]) {
      output[field] = new Date(output[field]);
    }
  }

  // ‚úÖ Only strip createdAt on updates
  if (protectCreatedAt && "createdAt" in output && output.id) {
    delete output.createdAt;
  }

  // ‚úÖ Always refresh updatedAt
  if (setUpdatedAt) {
    output.updatedAt = new Date();
  }

  return output as T;
}
// autoDateFields.ts
export const autoDateFields: Record<string, string[]> = {
  educationRecords: [], // none are actual Date columns, just yearFrom/yearCompleted (ints)
  shortCourse: ["startDate", "endDate"],
  professionalQualifications: ["startDate", "endDate"],
  employmentHistory: ["startDate", "endDate"],
  referees: [], // no dates
  documents: [], // no dates
};

