import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useAuth } from '@/hooks/useAuth';
import Navigation from '@/components/layout/Navigation';
import Sidebar from '@/components/layout/Sidebar';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Checkbox } from '@/components/ui/checkbox';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Switch } from '@/components/ui/switch';
import { MultiSelect } from '@/components/ui/multiselect';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { isUnauthorizedError } from '@/lib/authUtils';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { number, z } from 'zod';
import { Plus, Edit, Eye,Trash2, Upload,Calendar, Users,Search, Filter} from 'lucide-react';

// Zod schema for job form validation
export const jobSchema = z
  .object({
    title: z.string().min(5, "Job title must be at least 5 characters"),
    jg: z.coerce.number().min(1, "Please select a job group"),
    posts: z.coerce.number().min(1, "Please specify number of posts"),
    description: z
      .string()
      .min(20, "Description must be at least 20 characters"),
    departmentId: z.coerce.number().min(1, "Please select a department"),
    designationId: z.coerce.number().optional(),

    // ‚úÖ exact or progression
    progressionAllowed: z.boolean().optional().default(false),

    category: z.enum(["Open", "Internal",""], {
      errorMap: () => ({ message: "Please select category" }),
    }),

    // ‚úÖ numeric certificateLevel
    certificateLevel: z.coerce.number({
      required_error: "Please select certificate level",
      invalid_type_error: "Certificate level must be a number",
    }),

    requirements: z.string().optional(),

    // ‚úÖ study area and multiple specializations
    requiredStudyAreaId: z.coerce.number().optional(),
    requiredSpecializationIds: z.array(z.coerce.number()).optional().default([]),


    advertType: z.string().min(1, "Please specify advert type"),
    startDate: z.string().min(1, "Please select an application Start Date"),
    endDate: z.string().min(1, "Please select an application deadline"),
    advertNumb: z
      .string()
      .min(6, "Please Enter Advert Number (min 6 characters)"),

    experience: z.string().optional(),
    isActive: z.boolean().optional(),
  })
  .superRefine((data, ctx) => {
    if (data.category === "Internal") {
      if (!data.designationId) {
        ctx.addIssue({
          path: ["designationId"],
          code: z.ZodIssueCode.custom,
          message: "Designation is required when category is Internal",
        });
      }

      if (!data.experience) {
        ctx.addIssue({
          path: ["experience"],
          code: z.ZodIssueCode.custom,
          message: "Experience is required when category is Internal",
        });
      }

      if (!data.jg) {
        ctx.addIssue({
          path: ["jg"],
          code: z.ZodIssueCode.custom,
          message: "Job Group is required when category is Internal",
        });
      }

      if (!data.requiredStudyAreaId) {
        ctx.addIssue({
          path: ["requiredStudyAreaId"],
          code: z.ZodIssueCode.custom,
          message: "Study Area must be selected when category is Internal",
        });
      }
    }
  });

type JobFormData = z.infer<typeof jobSchema>;
export default function AdminJobManagement() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [searchTerm, setSearchTerm] = useState('');
  const [departmentFilter, setDepartmentFilter] = useState('all');
  const [statusFilter, setStatusFilter] = useState('all');
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [editingJob, setEditingJob] = useState<any>(null);
  // allow empty string so select can start blank
  const [category, setCategory] = useState<"OPEN" | "INTERNAL" | "">("");
  const [serial, setSerial] = useState(0); // You should fetch last serial from DB
  const [serials, setSerials] = useState({ OPEN: 0, INTERNAL: 0 });
  const advertNumber = generateAdvertNumber(category, serial);

  const { data: config, isLoading} = useQuery({
      queryKey: ['/api/public/config'],
      enabled: !!user && user.role === 'admin',
    });

    const configData = config || {} as any;

  const { data: applications = [] } = useQuery<any[]>({
    queryKey: ['/api/admin/applications'],
    enabled: !!user && user.role === 'admin',
  });

  const departments = configData?.departments || [];
  const designations = configData?.designations || [];
  const courses = configData?.courses || [];
  const certificateLevels = configData?.certificateLevels || [];
  const jobGroups = configData?.jobGroups || [];
  const jobs = configData?.jobs || [];
  const studyArea = configData?.studyAreas || [];
  const specializations = configData?.specializations; 



useEffect(() => {
  if (jobs && jobs.length > 0) {
    // Group serials by category
    const serialMap = { OPEN: 0, INTERNAL: 0 };

    jobs.forEach((job: any) => {
      // Match advert number pattern: TCPSB/OPEN/5/2025
      const year = new Date().getFullYear();
      const match = job.advertNumb?.match(new RegExp(`TCPSB\\/(OPEN|INTERNAL)\\/(\\d+)\\/${year}$`, "i"));

      if (match) {
        const category = match[1].toUpperCase() as "OPEN" | "INTERNAL";
        const serial = parseInt(match[2], 10);
        if (!isNaN(serial) && serial > (serialMap[category] || 0)) {
          serialMap[category] = serial;
        }
      }
    });

    setSerials(serialMap);
  } else {
    setSerials({ OPEN: 0, INTERNAL: 0 });
  }
}, [jobs]);

  const form = useForm<JobFormData>({
    resolver: zodResolver(jobSchema),
    defaultValues: {
      title: '',
      description: '',
      departmentId: 0,
      designationId: 0,
      jg: 0,
      certificateLevel: 0,
      requiredStudyAreaId: 0,
      posts: 0,
      experience: '',
      category: "",
      requirements: '',
      advertNumb: '',
      advertType: '',

    },
  });

  const createJobMutation = useMutation({
    mutationFn: async (data: JobFormData) => {
      // apiRequest already returns parsed JSON, no need to call .json() again
      return await apiRequest('POST', '/api/admin/jobs', {
        ...data,
        departmentId: (data.departmentId),
        posts: (data.posts),
        jg: (data.jg),
        designationId: data.designationId ? (data.designationId) : null,
        certificateLevel: (data.certificateLevel),
        requiredStudyAreaId: (data.requiredStudyAreaId),
        experience: (data.experience),
        advertType: (data.advertType),
        requirements: (data.requirements) || null,
      });
    },
    onSuccess: () => {
      toast({
        title: 'Job Created',
        description: 'Job posting has been created successfully.',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/public/config'] });
      queryClient.invalidateQueries({ queryKey: ['/api/public/jobs'] });
      setIsCreateModalOpen(false);
      form.reset();
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: 'Unauthorized',
          description: 'You are logged out. Logging in again...',
          variant: 'destructive',
        });
        setTimeout(() => {
          window.location.href = '/';
        }, 500);
        return;
      }
      toast({
        title: 'Error',
        description: error.message || 'Failed to create job',
        variant: 'destructive',
      });
    },
  });

  const updateJobMutation = useMutation({
    mutationFn: async ({ id, data }: { id: number; data: any }) => {
      return await apiRequest('PUT', `/api/admin/jobs/${id}`, data);
    },
    onSuccess: () => {
      toast({
        title: 'Job Updated',
        description: 'Job posting has been updated successfully.',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/public/config'] });
      queryClient.invalidateQueries({ queryKey: ['/api/public/jobs'] });
      setEditingJob(null);
      resetForm();
      setIsCreateModalOpen(false);
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: 'Unauthorized',
          description: 'You are logged out. Logging in again...',
          variant: 'destructive',
        });
        setTimeout(() => {
          window.location.href = '/';
        }, 500);
        return;
      }
      toast({
        title: 'Error',
        description: error.message || 'Failed to update job',
        variant: 'destructive',
      });
    },
  });

  const handleCreateJob = (data: JobFormData) => {

    if (editingJob) {
      // Update existing job with proper type conversion
      const processedData = {
        ...data,
        departmentId: (data.departmentId),
        posts: (data.posts),
        jg: (data.jg),
        designationId: data.designationId ? (data.designationId) : null,
        certificateLevel: (data.certificateLevel),
        requirements: (data.requirements) || null,
      };
      updateJobMutation.mutate({
        id: editingJob.id,
        data: processedData
      });
    } else {
      // Create new job
      createJobMutation.mutate(data);
    }
  };

  const handleEditJob = (job: any) => {
    setEditingJob(job);
    // Populate form with job data
    form.reset({
      title: job.title || '',
      description: job.description || '',
      departmentId: job.departmentId?.toString() || '',
      designationId: job.designationId?.toString() || '',
      jg: job.jg?.toString() || '',
      certificateLevel: job.certificateLevel || '',
      requiredStudyAreaId: job.requiredStudyAreaId || '',
      posts: job.posts?.toString() || '',
      experience: job.experience || '',
      category: job.category || '',
      requirements: job.requirements || '',
      advertNumb: job.advertNumb || '',
      advertType: job.advertType || '',
      startDate: job.startDate || '',
      endDate: job.endDate || '',
      isActive: job.isActive || false,
      requiredSpecializationIds: Array.isArray(job.requiredSpecializationIds)
        ? job.requiredSpecializationIds.map(Number)
        : job.requiredSpecializationIds
          ? [Number(job.requiredSpecializationIds)]
          : []
    });
    setIsCreateModalOpen(true);
  };

  const handleViewJob = (job: any) => {
    // Navigate to job details page or show job details modal
    // You can implement a view modal or redirect to details page
  };

  // Delete job mutation
  const deleteJobMutation = useMutation({
    mutationFn: async (jobId: number) => {
      return await apiRequest('DELETE', `/api/admin/jobs/${jobId}`);
    },
    onSuccess: () => {
      toast({
        title: 'Job Deleted',
        description: 'Job posting has been deleted successfully.',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/public/config'] });
      queryClient.invalidateQueries({ queryKey: ['/api/public/jobs'] });
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message || 'Failed to delete job',
        variant: 'destructive',
      });
    },
  });

  const handleDeleteJob = (jobId: number) => {
    if (confirm('Are you sure you want to delete this job? This action cannot be undone.')) {
      deleteJobMutation.mutate(jobId);
    }
  };

  const resetForm = () => {
    setEditingJob(null);
    form.reset({
      title: '',
      description: '',
      departmentId: 0,
      designationId: 0,
      jg: 0,
      certificateLevel: 0,
      requiredStudyAreaId: 0,
      posts: 0,
      experience: '',
      category: '',
      requirements: '',
      advertNumb: '',
      advertType: '',
      startDate: '',
      endDate: '',
      isActive: false
    });
  };

  const handleToggleJobStatus = (jobId: number, isActive: boolean) => {
    updateJobMutation.mutate({
      id: jobId,
      data: { isActive: !isActive }
    });
  };

  const filteredJobs = jobs.filter((job: any) => {
    const matchesSearch = job.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         job.description?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesDepartment = departmentFilter === 'all' || job.departmentId?.toString() === departmentFilter;
    const matchesStatus = statusFilter === 'all' || 
                         (statusFilter === 'active' && job.isActive) ||
                         (statusFilter === 'inactive' && !job.isActive);

    return matchesSearch && matchesDepartment && matchesStatus;
  });

  const getApplicationCount = (jobId: number) => {
    return applications.filter(app => app.job.id === jobId).length;
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-neutral-50">
        <Navigation />
        <div className="flex">
          <Sidebar userRole="admin" />
          <main className="flex-1 p-6">
            <div className="animate-pulse space-y-6">
              <div className="h-8 bg-gray-200 rounded w-1/3"></div>
              <div className="h-64 bg-gray-200 rounded"></div>
            </div>
          </main>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-neutral-50">
      <Navigation />

      <div className="flex">
        <Sidebar userRole="admin" />

        <main className="flex-1 p-6">
          <div className="max-w-7xl mx-auto">
            {/* Header */}
            <div className="flex justify-between items-center mb-8">
              <div>
                <h1 className="text-3xl font-bold text-gray-900 mb-2">Job Management</h1>
                <p className="text-gray-600">Create, edit, and manage job postings</p>
              </div>

              <Dialog open={isCreateModalOpen} onOpenChange={(open) => {
                setIsCreateModalOpen(open);
                if (!open) {
                  resetForm();
                }
              }}>
                <DialogTrigger asChild>
                  <Button onClick={() => resetForm()}>
                    <Plus className="w-4 h-4 mr-2" />
                    Post New Job
