      queryClient.invalidateQueries({ queryKey: ['/api/board/my-score'] });
      queryClient.invalidateQueries({ queryKey: ['/api/board/average-scores'] });
      setShowScoringDialog(false);
      resetScoreForm();
      setSelectedCandidate(null);
