import * as React from "react"
import useEmblaCarousel, {
  type UseEmblaCarouselType,
} from "embla-carousel-react"
import { ArrowLeft, ArrowRight } from "lucide-react"

import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"

type CarouselApi = UseEmblaCarouselType[1]
type UseCarouselParameters = Parameters<typeof useEmblaCarousel>
type CarouselOptions = UseCarouselParameters[0]
type CarouselPlugin = UseCarouselParameters[1]

type CarouselProps = {
  opts?: CarouselOptions
  plugins?: CarouselPlugin
  orientation?: "horizontal" | "vertical"
  setApi?: (api: CarouselApi) => void
}

type CarouselContextProps = {
  carouselRef: ReturnType<typeof useEmblaCarousel>[0]
  api: ReturnType<typeof useEmblaCarousel>[1]
  scrollPrev: () => void
  scrollNext: () => void
  canScrollPrev: boolean
  canScrollNext: boolean
} & CarouselProps

const CarouselContext = React.createContext<CarouselContextProps | null>(null)

function useCarousel() {
  const context = React.useContext(CarouselContext)

  if (!context) {
    throw new Error("useCarousel must be used within a <Carousel />")
  }

  return context
}

const Carousel = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement> & CarouselProps
>(
  (
    {
      orientation = "horizontal",
      opts,
      setApi,
      plugins,
      className,
      children,
      ...props
    },
    ref
  ) => {
    const [carouselRef, api] = useEmblaCarousel(
      {
        ...opts,
        axis: orientation === "horizontal" ? "x" : "y",
      },
      plugins
    )
    const [canScrollPrev, setCanScrollPrev] = React.useState(false)
    const [canScrollNext, setCanScrollNext] = React.useState(false)

    const onSelect = React.useCallback((api: CarouselApi) => {
      if (!api) {
        return
      }

      setCanScrollPrev(api.canScrollPrev())
      setCanScrollNext(api.canScrollNext())
    }, [])

    const scrollPrev = React.useCallback(() => {
      api?.scrollPrev()
    }, [api])

    const scrollNext = React.useCallback(() => {
      api?.scrollNext()
    }, [api])

    const handleKeyDown = React.useCallback(
      (event: React.KeyboardEvent<HTMLDivElement>) => {
        if (event.key === "ArrowLeft") {
          event.preventDefault()
          scrollPrev()
        } else if (event.key === "ArrowRight") {
          event.preventDefault()
          scrollNext()
        }
      },
      [scrollPrev, scrollNext]
    )

    React.useEffect(() => {
      if (!api || !setApi) {
        return
      }

      setApi(api)
    }, [api, setApi])

    React.useEffect(() => {
      if (!api) {
        return
      }

      onSelect(api)
      api.on("reInit", onSelect)
      api.on("select", onSelect)

      return () => {
        api?.off("select", onSelect)
      }
    }, [api, onSelect])

    return (
      <CarouselContext.Provider
        value={{
          carouselRef,
          api: api,
          opts,
          orientation:
            orientation || (opts?.axis === "y" ? "vertical" : "horizontal"),
          scrollPrev,
          scrollNext,
          canScrollPrev,
          canScrollNext,
        }}
      >
        <div
          ref={ref}
          onKeyDownCapture={handleKeyDown}
          className={cn("relative", className)}
          role="region"
          aria-roledescription="carousel"
          {...props}
        >
          {children}
        </div>
      </CarouselContext.Provider>
    )
  }
)
Carousel.displayName = "Carousel"

const CarouselContent = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const { carouselRef, orientation } = useCarousel()

  return (
    <div ref={carouselRef} className="overflow-hidden">
      <div
        ref={ref}
        className={cn(
          "flex",
          orientation === "horizontal" ? "-ml-4" : "-mt-4 flex-col",
          className
        )}
        {...props}
      />
    </div>
  )
})
CarouselContent.displayName = "CarouselContent"

const CarouselItem = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const { orientation } = useCarousel()

  return (
    <div
      ref={ref}
      role="group"
      aria-roledescription="slide"
      className={cn(
        "min-w-0 shrink-0 grow-0 basis-full",
        orientation === "horizontal" ? "pl-4" : "pt-4",
        className
      )}
      {...props}
    />
  )
})
CarouselItem.displayName = "CarouselItem"

const CarouselPrevious = React.forwardRef<
  HTMLButtonElement,
  React.ComponentProps<typeof Button>
>(({ className, variant = "outline", size = "icon", ...props }, ref) => {
  const { orientation, scrollPrev, canScrollPrev } = useCarousel()

  return (
    <Button
      ref={ref}
      variant={variant}
      size={size}
      className={cn(
        "absolute  h-8 w-8 rounded-full",
        orientation === "horizontal"
          ? "-left-12 top-1/2 -translate-y-1/2"
          : "-top-12 left-1/2 -translate-x-1/2 rotate-90",
        className
      )}
      disabled={!canScrollPrev}
      onClick={scrollPrev}
      {...props}
    >
      <ArrowLeft className="h-4 w-4" />
      <span className="sr-only">Previous slide</span>
    </Button>
  )
})
CarouselPrevious.displayName = "CarouselPrevious"

const CarouselNext = React.forwardRef<
  HTMLButtonElement,
  React.ComponentProps<typeof Button>
>(({ className, variant = "outline", size = "icon", ...props }, ref) => {
  const { orientation, scrollNext, canScrollNext } = useCarousel()

  return (
    <Button
      ref={ref}
      variant={variant}
      size={size}
      className={cn(
        "absolute h-8 w-8 rounded-full",
        orientation === "horizontal"
          ? "-right-12 top-1/2 -translate-y-1/2"
          : "-bottom-12 left-1/2 -translate-x-1/2 rotate-90",
        className
      )}
      disabled={!canScrollNext}
      onClick={scrollNext}
      {...props}
    >
      <ArrowRight className="h-4 w-4" />
      <span className="sr-only">Next slide</span>
    </Button>
  )
})
CarouselNext.displayName = "CarouselNext"

export {
  type CarouselApi,
  Carousel,
  CarouselContent,
  CarouselItem,
  CarouselPrevious,
  CarouselNext,
}

client/src/components/common/Documents.t.content:
import { useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useQuery } from "@tanstack/react-query";
import Navigation from "@/components/layout/Navigation";
import {
  Card,
  CardContent,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  Upload,
  FileText,
  CheckCircle,
  AlertCircle,
} from "lucide-react";
import { useFileUpload, uploadConfigs } from "@/hooks/useFileUpload";
import DocumentViewer from "@/components/documents/DocumentViewer";

type Document = {
  id: number;
  type: string;
  fileName: string;
  filePath: string;
  fileSize?: number;
  mimeType?: string;
  createdAt: string;
};

type ProfileResponse = {
  applicantProfile?: {
    documents?: Document[];
  };
};

type DocumentType = {
  id: string;
  label: string;
  required: boolean;
};

type ApplicantDocumentsProps = {
  columns?: "1" | "2" | "3"; // grid layout
  showNavigation?: boolean; // toggle Navigation
  mode?: "manage" | "view"; // upload or view only
  documentTypes?: DocumentType[]; // custom document types
  allowedFileTypes?: string[]; // accepted file types
};

const defaultDocumentTypes: DocumentType[] = [
  { id: "national_id", label: "National ID", required: true },
  { id: "birth_certificate", label: "Birth Certificate", required: true },
  { id: "academic_certificate", label: "Academic Certificate", required: true },
  { id: "academic_transcript", label: "Academic Transcript", required: true },
  { id: "professional_certificate", label: "Professional Certificate", required: false },
  { id: "experience_letter", label: "Experience Letter", required: false },
  { id: "clearance_certificate", label: "Police Clearance", required: true },
];

export default function ApplicantDocument({
  columns = "3",
  showNavigation = true,
  mode = "manage",
  documentTypes = defaultDocumentTypes,
  allowedFileTypes = [".pdf"],
}: ApplicantDocumentsProps) {
  const { user } = useAuth();
  const [viewingDocument, setViewingDocument] = useState<Document | null>(null);

  const { data: profile, isLoading } = useQuery<ProfileResponse>({
    queryKey: ["/api/auth/user"],
    enabled: !!user,
  });

  const documents = profile?.applicantProfile?.documents || [];

  const { uploadFile, state } = useFileUpload((uploadConfigs as any).documents);

  const handleFileUpload = async (file: File, type: string) => {
    if (mode === "manage") {
      await uploadFile(file, type, { type });
    }
  };

  const getDocumentStatus = (type: string) => {
    const document = documents.find((doc) => doc.type === type);
    if (document) {
      return { status: "uploaded", document };
    }
    return { status: "pending", document: null };
  };

  const formatFileSize = (bytes?: number) => {
    if (!bytes) return "Unknown size";
    const mb = bytes / (1024 * 1024);
    return `${mb.toFixed(1)} MB`;
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-neutral-50">
        {showNavigation && <Navigation />}
        <div className="flex justify-center items-center h-64">
          <div className="text-center">Loading...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-neutral-50">
      {showNavigation && <Navigation />}
      <div className="max-w-6xl mx-auto px-4 py-8">
        <div className="mb-6">
          <h1 className="text-2xl font-bold text-gray-900">Document Management</h1>
          <p className="text-gray-600">
            {mode === "manage"
              ? "Upload and manage your required documents"
              : "View uploaded documents"}
          </p>
        </div>

        <div
          className={`grid grid-cols-1 md:grid-cols-2 lg:grid-cols-${columns} gap-6`}
        >
          {documentTypes.map((docType) => {
            const { status, document } = getDocumentStatus(docType.id);
            const isUploading =
              state.uploadProgress[docType.id] || state.isUploading;

            return (
              <Card key={docType.id}>
                <CardHeader>
                  <div className="flex items-center justify-between">
                    <CardTitle className="text-lg">{docType.label}</CardTitle>
                    <div className="flex items-center space-x-2">
                      {docType.required && (
                        <Badge variant="secondary" className="text-xs">
                          Required
                        </Badge>
                      )}
                      {status === "uploaded" ? (
                        <Badge className="bg-green-100 text-green-800">
                          <CheckCircle className="w-3 h-3 mr-1" />
                          Uploaded
                        </Badge>
                      ) : (
                        <Badge
                          variant="outline"
                          className="text-orange-600 border-orange-300"
                        >
                          <AlertCircle className="w-3 h-3 mr-1" />
                          Pending
                        </Badge>
                      )}
                    </div>
                  </div>
                </CardHeader>
                <CardContent>
                  {document ? (
                    <div className="space-y-4">
                      <div className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg">
                        <FileText className="w-8 h-8 text-blue-600" />
                        <div className="flex-1">
                          <p className="font-medium text-gray-900">
                            {document.fileName}
                          </p>
                          <p className="text-sm text-gray-600">
                            {formatFileSize(document.fileSize)} ‚Ä¢{" "}
                            {new Date(document.createdAt).toLocaleDateString()}
                          </p>
                        </div>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => setViewingDocument(document)}
                        >
                          View
                        </Button>
                      </div>

                      {mode === "manage" && (
                        <div className="border-t pt-4">
                          <label
                            htmlFor={`replace-${docType.id}`}
                            className="block text-sm text-gray-700 mb-2"
                          >
                            Replace document:
                          </label>
                          <input
                            id={`replace-${docType.id}`}
                            type="file"
                            accept=".pdf,.jpg,.jpeg,.png"
                            onChange={(e) => {
                              const file = e.target.files?.[0];
                              if (file) handleFileUpload(file, docType.id);
                            }}
                            className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 
                              file:rounded-md file:border-0 file:text-sm file:font-medium 
                              file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                            disabled={isUploading}
                          />
                        </div>
                      )}
                    </div>
                  ) : (
                    mode === "manage" && (
                      <div className="text-center py-8 border-2 border-dashed border-gray-300 rounded-lg">
                        <Upload className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                        <p className="text-gray-600 mb-4">
                          Upload your {docType.label.toLowerCase()}
                        </p>
                        <label htmlFor={`upload-${docType.id}`}>
                          <Button variant="outline" disabled={isUploading} asChild>
                            <span>
                              {isUploading ? "Uploading..." : "Choose File"}
                            </span>
                          </Button>
                        </label>
                        <input
                          id={`upload-${docType.id}`}
                          type="file"
                          accept=".pdf"
                          onChange={(e) => {
                            const file = e.target.files?.[0];
                            if (file) handleFileUpload(file, docType.id);
                          }}
                          className="hidden"
                          disabled={isUploading}
                        />
                        <p className="text-xs text-gray-500 mt-2">
                          Supports {allowedFileTypes.join(", ")} (max 10MB)
                        </p>
                      </div>
                    )
                  )}
                </CardContent>
              </Card>
            );
          })}
        </div>

        {mode === "manage" && (
          <div className="mt-8 p-4 bg-blue-50 rounded-lg">
            <h3 className="font-medium text-blue-900 mb-2">Upload Guidelines</h3>
            <ul className="text-sm text-blue-800 space-y-1">
              <li>‚Ä¢ Ensure all documents are clear and legible</li>
              <li>‚Ä¢ Documents should be in PDF, JPG, JPEG, or PNG format</li>
              <li>‚Ä¢ Maximum file size is 10MB per document</li>
              <li>‚Ä¢ All required documents must be uploaded before submitting applications</li>
            </ul>
          </div>
        )}

        {viewingDocument && (
          <DocumentViewer
            document={viewingDocument}
            isOpen={!!viewingDocument}
            onClose={() => setViewingDocument(null)}
          />
        )}
      </div>
    </div>
  );
}

client/src/components/layout/Navigation..content:
import { useState } from 'react';
import { Link, useLocation } from 'wouter';
import { useAuth } from '@/hooks/useAuth';
import { Button } from '@/components/ui/button';
import { Sheet, SheetContent, SheetTrigger } from '@/components/ui/sheet';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import { Menu, Building, LogOut, User, Settings } from 'lucide-react';
import AuthDrawer from './AuthDrawer';

export default function Navigation() {
  const [location] = useLocation();
  const { user, isAuthenticated, logout } = useAuth();
  const [showAuthDrawer, setShowAuthDrawer] = useState(false);
  const [authMode, setAuthMode] = useState<'login' | 'signup' | 'otp' | 'mobile'>('login');

  const publicRoutes = [
    { path: '/', label: 'Home' },
    { path: '/about', label: 'About' },
    { path: '/gallery', label: 'Gallery' },
    { path: '/faqs', label: 'FAQs' },
    { path: '/notices', label: 'Notices' },
    { path: '/jobs', label: 'Jobs' },
  ];

  const handleAuthClick = (mode: 'login' | 'signup') => {
    setAuthMode(mode);
    setShowAuthDrawer(true);
  };

  const handleModeSwitch = (mode: 'login') => {
    setAuthMode(mode);
  };

  const handleLogout = () => {
    logout();
  };

  const getUserInitials = () => {    
    if (!user?.firstName && !user?.id) return 'U';
    return `${user?.firstName || ''}${user?.lastName || ''}`.toUpperCase();
  };

  const getDashboardUrl = () => {
    switch (user?.role) {
      case 'applicant': return '/dashboard';
      case 'admin': return '/admin';
      case 'board': return '/board';
      case 'accountant': return '/accountant';
      default: return '/';
    }
  };

  return (
    <>
      <nav className="bg-white shadow-sm border-b border-gray-200 sticky top-0 z-40">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            {/* Logo */}
            <Link href="/" className="flex items-center space-x-4">
              <div className="w-10 h-10 bg-primary rounded-lg flex items-center justify-center">
                <Building className="w-6 h-6 text-white" />
              </div>
              <div>
                <h1 className="text-lg font-semibold text-gray-900">TNCPSB</h1>
                <p className="text-xs text-gray-600">Job Portal</p>
              </div>
            </Link>

            {/* Desktop Navigation */}
            <div className="hidden md:flex items-center space-x-8">
              {publicRoutes.map((route) => (
                <Link
                  key={route.path}
                  href={route.path}
                  className={`font-medium transition-colors ${
                    location === route.path
                      ? 'text-primary'
                      : 'text-gray-700 hover:text-primary'
                  }`}
                >
                  {route.label}
                </Link>
              ))}
            </div>

            {/* Auth Section */}
            <div className="flex items-center space-x-4">
              {isAuthenticated ? (
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="ghost" className="relative h-8 w-8 rounded-full">
                      <Avatar className="h-8 w-8">
                        <AvatarImage src={user?.profileImageUrl} alt={user?.firstName} />
                        <AvatarFallback>{getUserInitials()}</AvatarFallback>
                      </Avatar>
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent className="w-56" align="end" forceMount>
                    <div className="flex items-center justify-start gap-2 p-2">
                      <div className="flex flex-col space-y-1 leading-none">
                        <p className="font-medium">{user?.firstName} {user?.lastName}</p>
                        <p className="w-[200px] truncate text-sm text-muted-foreground">
                          {user?.email}
                        </p>
                      </div>
                    </div>
                    <DropdownMenuSeparator />
                    <DropdownMenuItem asChild>
                      <Link href={getDashboardUrl()}>
                        <User className="mr-2 h-4 w-4" />
                        Dashboard
                      </Link>
                    </DropdownMenuItem>
                    {user?.role === 'applicant' && (
                      <DropdownMenuItem asChild>
                        <Link href="/profile">
                          <Settings className="mr-2 h-4 w-4" />
                          Profile
                        </Link>
                      </DropdownMenuItem>
                    )}
                    {user?.role === 'accountant' && (
                      <DropdownMenuItem asChild>
                        <Link href="/accountant/settings">
                          <Settings className="mr-2 h-4 w-4" />
                          Settings
                        </Link>
                      </DropdownMenuItem>
                    )}
                    {user?.role === 'admin' && (
                      <DropdownMenuItem asChild>
                        <Link href="/admin/settings">
                          <Settings className="mr-2 h-4 w-4" />
                          Settings
                        </Link>
                      </DropdownMenuItem>
                    )}
                    <DropdownMenuSeparator />
                    <DropdownMenuItem onClick={handleLogout}>
                      <LogOut className="mr-2 h-4 w-4" />
                      Log out
                    </DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>
              ) : (
                <>
                  <Button 
                    onClick={() => handleAuthClick('login')}
                    className="bg-primary hover:bg-blue-700"
                  >
                    Login
                  </Button>
                  <Button 
                    variant="outline" 
                    onClick={() => handleAuthClick('signup')}
                    className="border-primary text-primary hover:bg-blue-50"
                  >
                    Sign Up
                  </Button>
                </>
              )}

              {/* Mobile menu */}
              <Sheet>
                <SheetTrigger asChild>
                  <Button variant="ghost" size="icon" className="md:hidden">
                    <Menu className="h-5 w-5" />
                  </Button>
                </SheetTrigger>
                <SheetContent side="right">
                  <nav className="flex flex-col space-y-4 mt-8">
                    {publicRoutes.map((route) => (
                      <Link
                        key={route.path}
                        href={route.path}
                        className={`text-sm font-medium transition-colors ${
                          location === route.path
                            ? 'text-primary'
                            : 'text-gray-700 hover:text-primary'
                        }`}
                      >
                        {route.label}
                      </Link>
                    ))}
                  </nav>
                </SheetContent>
              </Sheet>
            </div>
          </div>
        </div>
      </nav>

      <AuthDrawer 
        open={showAuthDrawer} 
        onOpenChange={setShowAuthDrawer}
        mode={authMode}
        onModeChange={setAuthMode}
        handleClick={handleModeSwitch}
      />
    </>
  );
}

